    # –°–æ–∑–¥–∞—ë–º —Å–∏—Å—Ç–µ–º—É —á–∞—Å—Ç–∏—Ü
    system = ParticleSystem(particles=[])

    # –î–æ–±–∞–≤–ª—è–µ–º –Ω–µ—Å–∫–æ–ª—å–∫–æ —á–∞—Å—Ç–∏—Ü
    system.add_particle(Particle(position=Vector(0, 0, 0), velocity=Vector(1, 0.5, 0)))
    system.add_particle(Particle(position=Vector(10, 0, 0), velocity=Vector(-0.5, 0.2, 0), mass=2.0))

    print("–ù–∞—á–∞–ª—å–Ω—ã–µ –ø–æ–∑–∏—Ü–∏–∏:")
    for i, p in enumerate(system.particles):
        print(f"–ß–∞—Å—Ç–∏—Ü–∞ {i}: {p.position}")

    # –û–±–Ω–æ–≤–ª—è–µ–º —Å–∏—Å—Ç–µ–º—É (–ø—Ä–æ—à–ª–æ 1 —Å–µ–∫—É–Ω–¥–∞)
    system.update(dt=1.0)

    print("\n–ü–æ–∑–∏—Ü–∏–∏ –ø–æ—Å–ª–µ 1 —Å–µ–∫—É–Ω–¥—ã:")
    for i, p in enumerate(system.particles):
        print(f"–ß–∞—Å—Ç–∏—Ü–∞ {i}: {p.position}")
```

