## üß± –†–µ–∞–ª–∏–∑–∞—Ü–∏—è –Ω–∞ C —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º LLVM-C

```c
#include <stdio.h>
#include <llvm-c/Core.h>
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/Analysis.h>
#include <llvm-c/IRReader.h>
#include <llvm-c/Types.h>

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ fib(n)
void generate_fib_function(LLVMModuleRef module) {
    // 1. –ü–æ–ª—É—á–∞–µ–º —Ç–∏–ø—ã
    LLVMContextRef context = LLVMGetModuleContext(module);
    LLVMTypeRef i32_type = LLVMInt32TypeInContext(context);
    LLVMTypeRef func_type = LLVMFunctionType(i32_type, &i32_type, 1, 0);

    // 2. –°–æ–∑–¥–∞—ë–º —Ñ—É–Ω–∫—Ü–∏—é @fib
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", func_type);
    LLVMSetFunctionCallConv(fib_func, LLVMCCallConv);

    // 3. –°–æ–∑–¥–∞—ë–º –±–∞–∑–æ–≤—ã–µ –±–ª–æ–∫–∏
    LLVMBasicBlockRef entry = LLVMAppendBasicBlockInContext(context, fib_func, "entry");
    LLVMBasicBlockRef if_block = LLVMAppendBasicBlockInContext(context, fib_func, "if");
    LLVMBasicBlockRef then_block = LLVMAppendBasicBlockInContext(context, fib_func, "then");
    LLVMBasicBlockRef else_block = LLVMAppendBasicBlockInContext(context, fib_func, "else");
    LLVMBasicBlockRef for_body = LLVMAppendBasicBlockInContext(context, fib_func, "for.body");
    LLVMBasicBlockRef for_end = LLVMAppendBasicBlockInContext(context, fib_func, "for.end");

    // 4. –°–æ–∑–¥–∞—ë–º –±–∏–ª–¥–µ—Ä
    LLVMBuilderRef builder = LLVMCreateBuilderInContext(context);

    // 5. –í—Ö–æ–¥–Ω–æ–π –±–ª–æ–∫
    LLVMPositionBuilderAtEnd(builder, entry);
    LLVMValueRef n = LLVMGetParam(fib_func, 0);

    // –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º n <= 1
    LLVMValueRef cmp = LLVMBuildICmp(builder, LLVMIntSLE, n, LLVMConstInt(i32_type, 1, 0), "n_le_1");
    LLVMBuildCondBr(builder, cmp, then_block, else_block);

    // 6. –ë–ª–æ–∫ "then": return n
    LLVMPositionBuilderAtEnd(builder, then_block);
    LLVMBuildRet(builder, n);

    // 7. –ë–ª–æ–∫ "else": –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –ø–µ—Ä–µ–º–µ–Ω–Ω—ã—Ö
    LLVMPositionBuilderAtEnd(builder, else_block);
    // –°–æ–∑–¥–∞—ë–º –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ –≤ –ø–∞–º—è—Ç–∏: a, b, i
    LLVMValueRef a_ptr = LLVMBuildAlloca(builder, i32_type, "a");
    LLVMValueRef b_ptr = LLVMBuildAlloca(builder, i32_type, "b");
    LLVMValueRef i_ptr = LLVMBuildAlloca(builder, i32_type, "i");

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è: a = 0, b = 1, i = 2
    LLVMBuildStore(builder, LLVMConstInt(i32_type, 0, 0), a_ptr);
    LLVMBuildStore(builder, LLVMConstInt(i32_type, 1, 0), b_ptr);
    LLVMBuildStore(builder, LLVMConstInt(i32_type, 2, 0), i_ptr);

    // –ü–µ—Ä–µ—Ö–æ–¥ –≤ —Ç–µ–ª–æ —Ü–∏–∫–ª–∞
    LLVMBuildBr(builder, for_body);

    // 8. –¢–µ–ª–æ —Ü–∏–∫–ª–∞
    LLVMPositionBuilderAtEnd(builder, for_body);
    LLVMValueRef i_val = LLVMBuildLoad2(builder, i32_type, i_ptr, "i");
    LLVMValueRef cond = LLVMBuildICmp(builder, LLVMIntSLE, i_val, n, "i_le_n");
    LLVMBuildCondBr(builder, cond, for_body, for_end);

    // –ü–æ–≤—Ç–æ—Ä–Ω–æ –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä—É–µ–º –≤ –Ω–∞—á–∞–ª–æ for_body (—Ü–∏–∫–ª)
    LLVMPositionBuilderAtEnd(builder, for_body);
    LLVMValueRef a_val = LLVMBuildLoad2(builder, i32_type, a_ptr, "a");
    LLVMValueRef b_val = LLVMBuildLoad2(builder, i32_type, b_ptr, "b");
    LLVMValueRef next = LLVMBuildAdd(builder, a_val, b_val, "next");

    // –û–±–Ω–æ–≤–ª—è–µ–º: a = b, b = next
    LLVMBuildStore(builder, b_val, a_ptr);
    LLVMBuildStore(builder, next, b_ptr);

    // i = i + 1
    LLVMValueRef i_next = LLVMBuildAdd(builder, i_val, LLVMConstInt(i32_type, 1, 0), "i_next");
    LLVMBuildStore(builder, i_next, i_ptr);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º—Å—è –∫ —É—Å–ª–æ–≤–∏—é
    LLVMBuildBr(builder, for_body);

    // 9. –ë–ª–æ–∫ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è: –≤–æ–∑–≤—Ä–∞—â–∞–µ–º b
    LLVMPositionBuilderAtEnd(builder, for_end);
    LLVMValueRef result = LLVMBuildLoad2(builder, i32_type, b_ptr, "result");
    LLVMBuildRet(builder, result);

    // 10. –û—Å–≤–æ–±–æ–∂–¥–∞–µ–º –±–∏–ª–¥–µ—Ä
    LLVMDisposeBuilder(builder);

    // 11. –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ñ—É–Ω–∫—Ü–∏–∏ –Ω–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç—å
    char *error = NULL;
    if (LLVMVerifyFunction(fib_func, LLVMPrintMessageAction, &error)) {
        fprintf(stderr, "–û—à–∏–±–∫–∞ –≤–µ—Ä–∏—Ñ–∏–∫–∞—Ü–∏–∏ —Ñ—É–Ω–∫—Ü–∏–∏ fib: %s\n", error);
        LLVMDisposeMessage(error);
    }
}
```

