    generate_fib_function(module);

    // –í—ã–≤–æ–¥–∏–º IR –≤ stdout
    LLVMDumpModule(module);

    // –û–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ: —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –≤ —Ñ–∞–π–ª
    // LLVMWriteBitcodeToFile(module, "fib.bc");

    // –û—Å–≤–æ–±–æ–∂–¥–∞–µ–º –º–æ–¥—É–ª—å
    LLVMDisposeModule(module);

    return 0;
}
