entry:
  %n_le_1 = icmp sle i32 %0, 1
  br i1 %n_le_1, label %then, label %else

then:
  ret i32 %0

else:
  %a = alloca i32
  %b = alloca i32
  %i = alloca i32
  store i32 0, i32* %a
  store i32 1, i32* %b
  store i32 2, i32* %i
  br label %for.body

for.body:
  %i_val = load i32, i32* %i
  %i_le_n = icmp sle i32 %i_val, %0
  br i1 %i_le_n, label %for.body, label %for.end

