import numpy as np
import random
from collections import deque

# –õ–æ–≥–∏—Å—Ç–∏—á–µ—Å–∫–∞—è —Ä–µ–≥—Ä–µ—Å—Å–∏—è –∫–∞–∫ –º–æ–¥–µ–ª—å
class LogisticRegression:
    def __init__(self, input_dim, output_dim, lr=0.01):
        self.input_dim = input_dim
        self.output_dim = output_dim
        self.lr = lr
        self.W = np.random.randn(input_dim, output_dim) * 0.01
        self.b = np.zeros((1, output_dim))

    def sigmoid(self, z):
        # –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏—è z –¥–ª—è —Å—Ç–∞–±–∏–ª—å–Ω–æ—Å—Ç–∏
        z = np.clip(z, -500, 500)
        return 1 / (1 + np.exp(-z))

    def forward(self, X):
        z = X.dot(self.W) + self.b
        return self.sigmoid(z)

    def predict(self, X):
        return self.forward(X)

    def train(self, X, y):
        m = X.shape[0]
        y_pred = self.forward(X)
        # –ì—Ä–∞–¥–∏–µ–Ω—Ç—ã
        dz = (y_pred - y) / m
        dW = X.T.dot(dz)
        db = np.sum(dz, axis=0, keepdims=True)
        # –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
        self.W -= self.lr * dW
        self.b -= self.lr * db

# –ê–≥–µ–Ω—Ç DQN —Å –ª–æ–≥–∏—Å—Ç–∏—á–µ—Å–∫–æ–π —Ä–µ–≥—Ä–µ—Å—Å–∏–µ–π
class DQNAgent:
    def __init__(self, state_size, action_size):
        self.state_size = state_size
        self.action_size = action_size
        self.memory = deque(maxlen=2000)
        self.epsilon = 1.0
        self.epsilon_decay = 0.995
        self.epsilon_min = 0.01
        self.learning_rate = 0.01
        self.model = LogisticRegression(state_size, action_size, self.learning_rate)

    def remember(self, state, action, reward, next_state, done):
        self.memory.append((state, action, reward, next_state, done))

    def act(self, state):
        if np.random.rand() <= self.epsilon:
            return random.randrange(self.action_size)
        act_values = self.model.predict(state)
        return np.argmax(act_values[0])

    def replay(self, batch_size):
        if len(self.memory) < batch_size:
            return
        batch = random.sample(self.memory, batch_size)
        states = np.vstack([e[0] for e in batch])
        next_states = np.vstack([e[3] for e in batch])
        targets = self.model.predict(states)
        target_next = self.model.predict(next_states)
        for i, (state, action, reward, next_state, done) in enumerate(batch):
            target = reward
            if not done:
                target += 0.95 * np.amax(target_next[i])
            targets[i][action] = target
        self.model.train(states, targets)
        if self.epsilon > self.epsilon_min:
            self.epsilon *= self.epsilon_decay

# –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
if __name__ == "__main__":
    state_size = 4
    action_size = 2
    agent = DQNAgent(state_size, action_size)
    batch_size = 32

    # –°–∏–º—É–ª—è—Ü–∏—è –æ–±—É—á–µ–Ω–∏—è
    for e in range(1000):
        state = np.random.rand(1, state_size)
        for time in range(50):
            action = agent.act(state)
            next_state = np.random.rand(1, state_size)
            reward = np.random.rand()
            done = False
            if time == 49:
                done = True
            agent.remember(state, action, reward, next_state, done)
            state = next_state
            if done:
                print(f"episode: {e}, score: {time}, eps: {agent.epsilon:.2f}")
                break
        if len(agent.memory) > batch_size:
            agent.replay(batch_size)
