```java
import java.util.Scanner;

public class FractionCalculator {

    // –í–ª–æ–∂–µ–Ω–Ω—ã–π –∫–ª–∞—Å—Å –¥–ª—è –ø—Ä–µ–¥—Å—Ç–∞–≤–ª–µ–Ω–∏—è –¥—Ä–æ–±–∏
    static class Fraction {
        private int numerator;   // —á–∏—Å–ª–∏—Ç–µ–ª—å
        private int denominator; // –∑–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å

        // –ö–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä
        public Fraction(int numerator, int denominator) {
            if (denominator == 0) {
                throw new IllegalArgumentException("–ó–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –Ω—É–ª—ë–º.");
            }
            if (denominator < 0) {
                numerator = -numerator;
                denominator = -denominator;
            }
            this.numerator = numerator;
            this.denominator = denominator;
            simplify();
        }

        // –ù–û–î –¥–ª—è —Å–æ–∫—Ä–∞—â–µ–Ω–∏—è –¥—Ä–æ–±–∏
        private int gcd(int a, int b) {
            a = Math.abs(a);
            b = Math.abs(b);
            while (b != 0) {
                int temp = b;
                b = a % b;
                a = temp;
            }
            return a;
        }

        // –°–æ–∫—Ä–∞—â–µ–Ω–∏–µ –¥—Ä–æ–±–∏
        private void simplify() {
            int gcd = gcd(numerator, denominator);
            numerator /= gcd;
            denominator /= gcd;
        }

        // –°–ª–æ–∂–µ–Ω–∏–µ
        public Fraction add(Fraction other) {
            int newNumerator = this.numerator * other.denominator + other.numerator * this.denominator;
            int newDenominator = this.denominator * other.denominator;
            return new Fraction(newNumerator, newDenominator);
        }

        // –í—ã—á–∏—Ç–∞–Ω–∏–µ
        public Fraction subtract(Fraction other) {
            int newNumerator = this.numerator * other.denominator - other.numerator * this.denominator;
            int newDenominator = this.denominator * other.denominator;
            return new Fraction(newNumerator, newDenominator);
        }

        // –£–º–Ω–æ–∂–µ–Ω–∏–µ
        public Fraction multiply(Fraction other) {
            int newNumerator = this.numerator * other.numerator;
            int newDenominator = this.denominator * other.denominator;
            return new Fraction(newNumerator, newDenominator);
        }

        // –î–µ–ª–µ–Ω–∏–µ
        public Fraction divide(Fraction other) {
            if (other.numerator == 0) {
                throw new ArithmeticException("–î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å.");
            }
            int newNumerator = this.numerator * other.denominator;
            int newDenominator = this.denominator * other.numerator;
            return new Fraction(newNumerator, newDenominator);
        }

        // –ü–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ toString –¥–ª—è –∫—Ä–∞—Å–∏–≤–æ–≥–æ –≤—ã–≤–æ–¥–∞
        @Override
        public String toString() {
            if (denominator == 1) {
                return String.valueOf(numerator);
            }
            return numerator + "/" + denominator;
        }
    }

    // –ú–µ—Ç–æ–¥ –¥–ª—è –ø–∞—Ä—Å–∏–Ω–≥–∞ —Å—Ç—Ä–æ–∫–∏ –≤–∏–¥–∞ "a/b" –∏–ª–∏ "a"
    public static Fraction parseFraction(String input) {
        input = input.trim();
        if (input.contains("/")) {
            String[] parts = input.split("/");
            int num = Integer.parseInt(parts[0].trim());
            int den = Integer.parseInt(parts[1].trim());
            return new Fraction(num, den);
        } else {
            return new Fraction(Integer.parseInt(input), 1);
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.println("=== –ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –¥—Ä–æ–±–µ–π ===");
        System.out.println("–§–æ—Ä–º–∞—Ç –≤–≤–æ–¥–∞: —á–∏—Å–ª–∏—Ç–µ–ª—å/–∑–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å (–Ω–∞–ø—Ä–∏–º–µ—Ä: 1/2) –∏–ª–∏ —Ü–µ–ª–æ–µ —á–∏—Å–ª–æ (–Ω–∞–ø—Ä–∏–º–µ—Ä: 3)");
        System.out.println("–î–æ—Å—Ç—É–ø–Ω—ã–µ –æ–ø–µ—Ä–∞—Ü–∏–∏: +, -, *, /");

        while (true) {
            try {
                System.out.print("–í–≤–µ–¥–∏—Ç–µ –ø–µ—Ä–≤—É—é –¥—Ä–æ–±—å: ");
                String input1 = scanner.nextLine();
                Fraction frac1 = parseFraction(input1);

                System.out.print("–í–≤–µ–¥–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é (+, -, *, /): ");
                String op = scanner.nextLine().trim();

                System.out.print("–í–≤–µ–¥–∏—Ç–µ –≤—Ç–æ—Ä—É—é –¥—Ä–æ–±—å: ");
                String input2 = scanner.nextLine();
                Fraction frac2 = parseFraction(input2);

                Fraction result = null;

                switch (op) {
                    case "+":
                        result = frac1.add(frac2);
                        break;
                    case "-":
                        result = frac1.subtract(frac2);
                        break;
                    case "*":
                        result = frac1.multiply(frac2);
                        break;
                    case "/":
                        result = frac1.divide(frac2);
                        break;
                    default:
                        System.out.println("–ù–µ–≤–µ—Ä–Ω–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è. –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ +, -, *, /");
                        continue;
                }

                System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç: " + result);
            } catch (Exception e) {
                System.out.println("–û—à–∏–±–∫–∞: " + e.getMessage());
            }

            System.out.print("–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å? (–¥–∞/–Ω–µ—Ç): ");
            String cont = scanner.nextLine().trim().toLowerCase();
            if (cont.equals("–Ω–µ—Ç") || cont.equals("no") || cont.equals("n")) {
                break;
            }
        }

        System.out.println("–î–æ —Å–≤–∏–¥–∞–Ω–∏—è!");
        scanner.close();
    }
}
