### üîπ –ü—Ä–∏–º–µ—Ä 1: –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ `decimal` –∏ —Ñ–æ—Ä–º—É–ª—ã –ú–∞—á–∏–Ω–∞ (–¥–æ ~28 –∑–Ω–∞–∫–æ–≤)

```csharp
using System;
using System.Numerics;

class Program
{
    // –í—ã—á–∏—Å–ª–µ–Ω–∏–µ –∞—Ä–∫—Ç–∞–Ω–≥–µ–Ω—Å–∞ —Å –ø–æ–º–æ—â—å—é —Ä—è–¥–∞ –¢–µ–π–ª–æ—Ä–∞
    static decimal ArcTan(decimal x, int iterations)
    {
        decimal result = 0;
        decimal term = x;
        decimal xSquared = x * x;
        decimal denominator = 1;

        for (int i = 0; i < iterations; i++)
        {
            if (i % 2 == 0)
                result += term;
            else
                result -= term;

            term *= xSquared;
            term /= (denominator += 2);
        }

        return result;
    }

    static void Main()
    {
        // –§–æ—Ä–º—É–ª–∞ –ú–∞—á–∏–Ω–∞: œÄ/4 = 4 * arctan(1/5) - arctan(1/239)
        decimal oneFifth = 1.0m / 5.0m;
        decimal oneTwoThirtyNine = 1.0m / 239.0m;

        int iterations = 50; // –î–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –¥–ª—è 28 –∑–Ω–∞–∫–æ–≤

        decimal piOver4 = 4 * ArcTan(oneFifth, iterations) - ArcTan(oneTwoThirtyNine, iterations);
        decimal pi = 4 * piOver4;

        Console.WriteLine("–í—ã—á–∏—Å–ª–µ–Ω–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ œÄ:");
        Console.WriteLine(pi);
        Console.WriteLine($"–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–Ω–∞–∫–æ–≤ –ø–æ—Å–ª–µ –∑–∞–ø—è—Ç–æ–π: {GetDecimalPlaces(pi)}");
    }

    static int GetDecimalPlaces(decimal value)
    {
        string s = value.ToString();
        int index = s.IndexOf('.');
        return index == -1 ? 0 : s.Length - index - 1;
    }
}
```

