### üîπ –ü—Ä–∏–º–µ—Ä 2: –í—ã—Å–æ–∫–∞—è —Ç–æ—á–Ω–æ—Å—Ç—å (—Å–æ—Ç–Ω–∏ –∑–Ω–∞–∫–æ–≤) ‚Äî —á–µ—Ä–µ–∑ —Ä—è–¥ –ß—É–¥–Ω–æ–≤—Å–∫–æ–≥–æ (—É–ø—Ä–æ—â—ë–Ω–Ω–∞—è –≤–µ—Ä—Å–∏—è)

–î–ª—è –±–æ–ª—å—à–µ–π —Ç–æ—á–Ω–æ—Å—Ç–∏ (—Å–æ—Ç–Ω–∏ –∏–ª–∏ —Ç—ã—Å—è—á–∏ –∑–Ω–∞–∫–æ–≤) –Ω—É–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å `BigInteger`. –ù–∏–∂–µ ‚Äî —É–ø—Ä–æ—â—ë–Ω–Ω–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è —Ä—è–¥–∞ –ß—É–¥–Ω–æ–≤—Å–∫–æ–≥–æ:

```csharp
using System;
using System.Numerics;

class PiCalculator
{
    public static string ComputePi(int digits)
    {
        // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–Ω–∞–∫–æ–≤
        int n = digits / 14 + 1; // –ö–∞–∂–¥—ã–π —á–ª–µ–Ω –¥–∞—ë—Ç ~14 –∑–Ω–∞–∫–æ–≤

        BigInteger p = 0;
        BigInteger q = 0;

        BigInteger a = BigInteger.One;
        BigInteger b = 0;
        BigInteger c = BigInteger.Pow(640320, 3);
        BigInteger d = c / 256;

        for (int k = 0; k < n; k++)
        {
            if (k > 0)
            {
                a *= -(6 * k - 5) * (2 * k - 1) * (6 * k - 1);
                a /= k * k * k * 24591257856;
            }

            b += a * (545140134 * k + 13591409);
            c += a * (k + 1 / 2); // –£–ø—Ä–æ—â—ë–Ω–Ω–æ, –Ω–∞ —Å–∞–º–æ–º –¥–µ–ª–µ –Ω—É–∂–Ω–æ —Ç–æ—á–Ω–µ–µ
        }

        p = b;
        q = c;

        // 1/œÄ = 12 / sqrt(640320^3) * sum(...)
        // œÄ = sqrt(640320^3) / (12 * sum)

        BigInteger sqrt = SqrtBigInteger(BigInteger.Pow(640320, 3), digits);
        BigInteger piNumerator = sqrt * 100; // –º–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º
        BigInteger piDenominator = 12 * p;

        return DivideBigInteger(piNumerator, piDenominator, digits);
    }

    // –ü—Ä–æ—Å—Ç–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è –∏–∑–≤–ª–µ—á–µ–Ω–∏—è –∫–≤–∞–¥—Ä–∞—Ç–Ω–æ–≥–æ –∫–æ—Ä–Ω—è –∏–∑ BigInteger
    public static BigInteger SqrtBigInteger(BigInteger n, int digits)
    {
        BigInteger x = n;
        int scale = (int)Math.Pow(10, digits);
        n *= scale;

        for (int i = 0; i < 100; i++)
        {
            x = (x + n / x) / 2;
        }

        return x / scale;
    }

    // –î–µ–ª–µ–Ω–∏–µ BigInteger —Å –∑–∞–¥–∞–Ω–Ω–æ–π —Ç–æ—á–Ω–æ—Å—Ç—å—é
    public static string DivideBigInteger(BigInteger a, BigInteger b, int digits)
    {
        BigInteger quotient = a / b;
        BigInteger remainder = a % b;

        string result = quotient.ToString() + ".";
        remainder *= 10;

        for (int i = 0; i < digits; i++)
        {
            BigInteger digit = remainder / b;
            result += (char)('0' + digit);
            remainder = (remainder % b) * 10;
        }

        return result;
    }

    static void Main()
    {
        Console.WriteLine("–°–∫–æ–ª—å–∫–æ –∑–Ω–∞–∫–æ–≤ –ø–æ—Å–ª–µ –∑–∞–ø—è—Ç–æ–π –≤—ã —Ö–æ—Ç–∏—Ç–µ? ");
        if (int.TryParse(Console.ReadLine(), out int digits))
        {
            string pi = ComputePi(digits);
            Console.WriteLine($"œÄ = {pi}");
        }
    }
}
```

