        String secondInput = scanner.nextLine();

        try {
            Fraction first = parseFraction(firstInput);
            Fraction second = parseFraction(secondInput);

            System.out.println("–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é (+, -, *, /): ");
            String operation = scanner.nextLine().trim();

            switch (operation) {
                case "+":
