```csharp
using System;
using System.Text;

class Program
{
    static void Main()
    {
        int digits = 10; // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–Ω–∞–∫–æ–≤ –ø–æ—Å–ª–µ –∑–∞–ø—è—Ç–æ–π
        double piApproximation = CalculatePi(digits);
        Console.WriteLine($"–ü—Ä–∏–±–ª–∏–∂–µ–Ω–∏–µ œÄ —Å {digits} –∑–Ω–∞–∫–∞–º–∏: " + piApproximation.ToString("F" + digits));
    }

    static double CalculatePi(int digits)
    {
        double pi = 0;
        int sign = 1;
        int term = 1;
        int denominator = 1;

        for (int i = 0; i < 1e6; i++) // –£–≤–µ–ª–∏—á—å—Ç–µ –∏—Ç–µ—Ä–∞—Ü–∏–∏ –¥–ª—è –±–æ–ª—å—à–µ–≥–æ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ –∑–Ω–∞–∫–æ–≤
        {
            pi += sign * (1.0 / denominator);
            sign *= -1;
            denominator += 2 * (i + 1);
        }

        return pi * 4;
    }
}
```

### 2. –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –±–∏–±–ª–∏–æ—Ç–µ–∫–∏ Math.NET Numerics (–±—ã—Å—Ç—Ä—ã–π –∏ —Ç–æ—á–Ω—ã–π)
