```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// –°—Ç—Ä—É–∫—Ç—É—Ä–∞ –∑–∞–¥–∞—á–∏
typedef struct {
    void (*task_func)(void*);
    void* arg;
    int priority;
} Task;

// –ì–ª–æ–±–∞–ª—å–Ω—ã–µ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ
Task tasks[10]; // –ú–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–¥–∞—á
int task_count = 0;
int current_task_index = 0;

// –§—É–Ω–∫—Ü–∏—è —Å–æ–∑–¥–∞–Ω–∏—è –∑–∞–¥–∞—á–∏
void create_task(void (*func)(void*), void* arg, int priority) {
    if (task_count >= sizeof(tasks)/sizeof(Task)) {
        printf("–û—à–∏–±–∫–∞: –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–¥–∞—á –¥–æ—Å—Ç–∏–≥–Ω—É—Ç–æ\n");
        return;
    }

    memcpy(&tasks[task_count], &Task{
        .task_func = func,
        .arg = arg,
        .priority = priority
    }, sizeof(Task));
    task_count++;
}

// –§—É–Ω–∫—Ü–∏—è –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è –∑–∞–¥–∞—á
void switch_context() {
    // –ü—Ä–æ—Å—Ç–æ–µ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –º–µ–∂–¥—É –¥–≤—É–º—è –∑–∞–¥–∞—á–∞–º–∏
    static int next_task = 0;
    current_task_index = (current_task_index + 1) % task_count;
    next_task = (next_task + 1) % task_count;

    // –í —Ä–µ–∞–ª—å–Ω–æ–π —Å–∏—Å—Ç–µ–º–µ –∑–¥–µ—Å—å –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –∫–æ–¥ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è/–≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è —Ä–µ–≥–∏—Å—Ç—Ä–æ–≤
    printf("–ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–æ –Ω–∞ –∑–∞–¥–∞—á—É %d\n", current_task_index);
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á–∏
void task1(void* arg) {
    for(int i=0; i<5; i++) {
        printf("–ó–∞–¥–∞—á–∞ 1: %d\n", i);
        switch_context();
    }
}

void task2(void* arg) {
    for(int i=0; i<5; i++) {
        printf("–ó–∞–¥–∞—á–∞ 2: %d\n", i);
        switch_context();
    }
}

int main() {
    // –°–æ–∑–¥–∞–µ–º –∑–∞–¥–∞—á–∏
    create_task(task1, NULL, 1);
    create_task(task2, NULL, 2);

    // –ó–∞–ø—É—Å–∫ —Ü–∏–∫–ª–∞ –∑–∞–¥–∞—á
    while(1) {
        // –í—ã–ø–æ–ª–Ω–µ–Ω–∏–µ —Ç–µ–∫—É—â–µ–π –∑–∞–¥–∞—á–∏
        tasks[current_task_index].task_func(NULL);

        // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –∑–∞–¥–∞—á–∏
        switch_context();
    }

    return 0;
}
```

