        System.out.print("–í–≤–µ–¥–∏—Ç–µ –ø–µ—Ä–≤—É—é –¥—Ä–æ–±—å (–≤ —Ñ–æ—Ä–º–∞—Ç–µ a/b): ");
        String input1 = scanner.nextLine();

        // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç–∏ –≤–≤–æ–¥–∞ –ø–µ—Ä–≤–æ–π –¥—Ä–æ–±–∏
        if (!validateFraction(input1)) {
            System.out.println("–û—à–∏–±–∫–∞: –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏.");
            return;
        }

        int numerator1 = Integer.parseInt(input1.split("/")[0]);
        int denominator1 = Integer.parseInt(input1.split("/")[1]);

        // –í–≤–æ–¥ –≤—Ç–æ—Ä–æ–π –¥—Ä–æ–±–∏
        System.out.print("–í–≤–µ–¥–∏—Ç–µ –≤—Ç–æ—Ä—É—é –¥—Ä–æ–±—å (–≤ —Ñ–æ—Ä–º–∞—Ç–µ a/b): ");
        String input2 = scanner.nextLine();

        // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç–∏ –≤–≤–æ–¥–∞ –≤—Ç–æ—Ä–æ–π –¥—Ä–æ–±–∏
        if (!validateFraction(input2)) {
            System.out.println("–û—à–∏–±–∫–∞: –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏.");
            return;
        }

        int numerator2 = Integer.parseInt(input2.split("/")[0]);
        int denominator2 = Integer.parseInt(input2.split("/")[1]);

        // –í—ã–±–æ—Ä –æ–ø–µ—Ä–∞—Ü–∏–∏
        System.out.println("\n–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é:");
        System.out.println("1. –°–ª–æ–∂–µ–Ω–∏–µ");
        System.out.println("2. –í—ã—á–∏—Ç–∞–Ω–∏–µ");
        System.out.println("3. –£–º–Ω–æ–∂–µ–Ω–∏–µ");
        System.out.println("4. –î–µ–ª–µ–Ω–∏–µ");
        System.out.print("–í–≤–µ–¥–∏—Ç–µ –Ω–æ–º–µ—Ä –æ–ø–µ—Ä–∞—Ü–∏–∏: ");
        int operation = scanner.nextInt();
        scanner.nextLine(); // –û—á–∏—Å—Ç–∫–∞ –±—É—Ñ–µ—Ä–∞

        // –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ–ø–µ—Ä–∞—Ü–∏–∏
        switch (operation) {
            case 1:
                System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç —Å–ª–æ–∂–µ–Ω–∏—è: " + addFractions(numerator1, denominator1, numerator2, denominator2));
                break;
            case 2:
                System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç –≤—ã—á–∏—Ç–∞–Ω–∏—è: " + subtractFractions(numerator1, denominator1, numerator2, denominator2));
                break;
            case 3:
                System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç —É–º–Ω–æ–∂–µ–Ω–∏—è: " + multiplyFractions(numerator1, denominator1, numerator2, denominator2));
                break;
            case 4:
                if (denominator2 == 0) {
                    System.out.println("–û—à–∏–±–∫–∞: –î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ.");
                    return;
                }
                System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç –¥–µ–ª–µ–Ω–∏—è: " + divideFractions(numerator1, denominator1, numerator2, denominator2));
                break;
            default:
                System.out.println("–û—à–∏–±–∫–∞: –ù–µ–≤–µ—Ä–Ω—ã–π –≤—ã–±–æ—Ä –æ–ø–µ—Ä–∞—Ü–∏–∏.");
        }

        scanner.close();
    }

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç–∏ –≤–≤–æ–¥–∞ –¥—Ä–æ–±–∏
    private static boolean validateFraction(String input) {
        try {
            String[] parts = input.split("/");
            if (parts.length != 2 || parts[0].isEmpty() || parts[1].isEmpty()) {
                return false;
            }
            int num = Integer.parseInt(parts[0]);
            int den = Integer.parseInt(parts[1]);
            return den != 0;
        } catch (NumberFormatException e) {
            return false;
        }
    }

    // –°–ª–æ–∂–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    private static String addFractions(int a, int b, int c, int d) {
        int resultNumerator = a * d + c * b;
        int resultDenominator = b * d;
        return simplify(resultNumerator, resultDenominator);
    }

    // –í—ã—á–∏—Ç–∞–Ω–∏–µ –¥—Ä–æ–±–µ–π
    private static String subtractFractions(int a, int b, int c, int d) {
        int resultNumerator = a * d - c * b;
        int resultDenominator = b * d;
        return simplify(resultNumerator, resultDenominator);
    }

    // –£–º–Ω–æ–∂–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    private static String multiplyFractions(int a, int b, int c, int d) {
        int resultNumerator = a * c;
        int resultDenominator = b * d;
        return simplify(resultNumerator, resultDenominator);
    }

    // –î–µ–ª–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    private static String divideFractions(int a, int b, int c, int d) {
        if (d == 0) {
            return "–û—à–∏–±–∫–∞: –î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ.";
        }
        int resultNumerator = a * d;
        int resultDenominator = b * c;
        return simplify(resultNumerator, resultDenominator);
    }

    // –£–ø—Ä–æ—â–µ–Ω–∏–µ –¥—Ä–æ–±–∏
    private static String simplify(int numerator, int denominator) {
        int gcd = gcd(Math.abs(numerator), Math.abs(denominator));
        numerator /= gcd;
        denominator /= gcd;
        if (denominator < 0) {
            numerator *= -1;
            denominator *= -1;
        }
        if (numerator == 0) {
            return "0";
        }
        return numerator + "/" + denominator;
    }

    // –†–∞—Å—á–µ—Ç –Ω–∞–∏–±–æ–ª—å—à–µ–≥–æ –æ–±—â–µ–≥–æ –¥–µ–ª–∏—Ç–µ–ª—è
    private static int gcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }
}
