–•–æ—Ç–∏—Ç–µ, —á—Ç–æ–±—ã —è –¥–æ–±–∞–≤–∏–ª –∫–∞–∫–∏–µ-—Ç–æ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏?

–í–æ—Ç –ø—Ä–∏–º–µ—Ä –∫–æ–¥–∞ –Ω–∞ C# –¥–ª—è –≤—ã—á–∏—Å–ª–µ–Ω–∏—è –¥–µ—Å—è—Ç–∏—á–Ω—ã—Ö –¥–æ–ª–µ–π —á–∏—Å–ª–∞ œÄ —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º –º–µ—Ç–æ–¥–∞ –ú–æ–Ω—Ç–µ–∫–∞—Ä–ª–æ:

```csharp
using System;

class Program
{
    static void Main()
    {
        Console.Write("–í–≤–µ–¥–∏—Ç–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∏—Ç–µ—Ä–∞—Ü–∏–π: ");
        int iterations = int.Parse(Console.ReadLine());

        double countInsideCircle = 0;

        Random random = new Random();

        for (int i = 0; i < iterations; i++)
        {
            double x = random.NextDouble(); // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Å–ª—É—á–∞–π–Ω–æ–≥–æ —á–∏—Å–ª–∞ –æ—Ç 0 –¥–æ 1
            double y = random.NextDouble();

            if (x * x + y * y <= 1) // –ü—Ä–æ–≤–µ—Ä–∫–∞, –ø–æ–ø–∞–¥–∞–µ—Ç –ª–∏ —Ç–æ—á–∫–∞ –≤ –∫—Ä—É–≥ —Ä–∞–¥–∏—É—Å–æ–º 1
            {
                countInsideCircle++;
            }
        }

        double piEstimate = 4 * countInsideCircle / iterations;
        Console.WriteLine($"–ü—Ä–∏–±–ª–∏–∂–µ–Ω–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ œÄ: {piEstimate}");
    }
}
