–≠—Ç–æ—Ç –ø–æ–¥—Ö–æ–¥ —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–µ–Ω –∏ —á–∏—Ç–∞–±–µ–ª–µ–Ω, –æ—Å–æ–±–µ–Ω–Ω–æ –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å —Å—Ç—Ä–æ–∫–∞–º–∏ –≤ Java.

–í–æ—Ç –ø—Ä–∏–º–µ—Ä –±–∞–∑–æ–≤–æ–≥–æ –æ–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–æ–≥–æ —Å–∏—Å—Ç–µ–º–Ω–æ–≥–æ –∫–æ–¥–∞, –∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ–≥–æ FreeRTOS, —Å —Ñ–æ–∫—É—Å–æ–º –Ω–∞ —É–ø—Ä–∞–≤–ª–µ–Ω–∏–∏ –∑–∞–¥–∞—á–∞–º–∏, –æ—á–µ—Ä–µ–¥—è–º–∏ –∏ —Å–µ–º–∞—Ñ–æ—Ä–∞–º–∏. –≠—Ç–æ—Ç –∫–æ–¥ —Ä–µ–∞–ª–∏–∑—É–µ—Ç –º–∏–Ω–∏–º–∞–ª—å–Ω—É—é –≤–µ—Ä—Å–∏—é –û–°, –∫–æ—Ç–æ—Ä–∞—è –º–æ–∂–µ—Ç –±—ã—Ç—å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∞ –¥–ª—è –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏–∏ –ø—Ä–∏–Ω—Ü–∏–ø–æ–≤ —Ä–∞–±–æ—Ç—ã.

```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <pthread.h>
#include <semaphore.h>
#include <queue.h>

#define MAX_TASKS 4
#define MAX_QUEUE_SIZE 10
#define MAX_SEMAPHORES 2

// –û—Å–Ω–æ–≤–Ω—ã–µ —Å—Ç—Ä—É–∫—Ç—É—Ä—ã
typedef struct {
    int id;
    char name[32];
    int priority;
    void (*func)(void*);
    void* arg;
    int state; // 0 - –Ω–µ –∞–∫—Ç–∏–≤–µ–Ω, 1 - –∞–∫—Ç–∏–≤–µ–Ω
    pthread_t thread;
} Task;

typedef struct {
    int id;
    int count;
    int max_count;
    int* data;
} Queue;

typedef struct {
    int id;
    int count;
    int max_count;
    int* data;
} Semaphore;

// –°—Ç—Ä—É–∫—Ç—É—Ä–∞ –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –ø–∞–º—è—Ç—å—é
typedef struct {
    int* heap;
    int heap_size;
    int heap_free;
} Memory;

// –°—Ç—Ä—É–∫—Ç—É—Ä–∞ –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∫—ç—à–µ–º
typedef struct {
    int* cache;
    int cache_size;
    int cache_used;
} Cache;

// –û—Å–Ω–æ–≤–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏
Task* create_task(int priority, void (*func)(void*), void* arg) {
    Task* task = (Task*)malloc(sizeof(Task));
    if (!task) return NULL;
    task->id = ++global_id;
    task->priority = priority;
    task->func = func;
    task->arg = arg;
    task->state = 0;
    pthread_create(&task->thread, NULL, (void*)task_func, task);
    return task;
}

Queue* create_queue(int max_size) {
    Queue* q = (Queue*)malloc(sizeof(Queue));
    if (!q) return NULL;
    q->id = ++global_id;
    q->count = 0;
    q->max_count = max_size;
    q->data = (int*)malloc(max_size * sizeof(int));
    return q;
}

Semaphore* create_semaphore(int max_count) {
    Semaphore* s = (Semaphore*)malloc(sizeof(Semaphore));
    if (!s) return NULL;
    s->id = ++global_id;
    s->count = 0;
    s->max_count = max_count;
    return s;
}

void* task_func(void* arg) {
    Task* task = (Task*)arg;
    while (task->state) {
        printf("Task %d started\n", task->id);
        task->func(task->arg);
        usleep(100000); // –ó–∞–¥–µ—Ä–∂–∫–∞ –¥–ª—è –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏–∏
    }
    return NULL;
}

void queue_send(Queue* q, int value) {
    if (q->count < q->max_count) {
        q->data[q->count++] = value;
        printf("Queue %d: %d added\n", q->id, value);
    } else {
        printf("Queue %d: full\n", q->id);
    }
}

int queue_receive(Queue* q) {
    if (q->count > 0) {
        int value = q->data[0];
        q->data[0] = 0;
        q->count--;
        printf("Queue %d: %d received\n", q->id, value);
        return value;
    }
    return -1;
}

void semaphore_acquire(Semaphore* s) {
    if (s->count > 0) {
        s->count--;
        printf("Semaphore %d acquired\n", s->id);
    } else {
        printf("Semaphore %d not acquired\n", s->id);
    }
}

void semaphore_release(Semaphore* s) {
    s->count++;
    printf("Semaphore %d released\n", s->id);
}

// –ö—ç—à –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞ –∫ –¥–∞–Ω–Ω—ã–º
Cache* create_cache(int size) {
    Cache* c = (Cache*)malloc(sizeof(Cache));
    if (!c) return NULL;
    c->cache_size = size;
    c->cache_used = 0;
    c->cache = (int*)malloc(size * sizeof(int));
    return c;
}

void cache_clear(Cache* c) {
    if (c) {
        memset(c->cache, 0, c->cache_size * sizeof(int));
        c->cache_used = 0;
    }
}

// –ü–∞–º—è—Ç—å –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Å—Ç–µ–∫–æ–≤ –∑–∞–¥–∞—á
Memory* create_memory(int size) {
    Memory* m = (Memory*)malloc(sizeof(Memory));
    if (!m) return NULL;
    m->heap = (int*)malloc(size * sizeof(int));
    m->heap_size = size;
    m->heap_free = size;
    return m;
}

void memory_free(Memory* m) {
    if (m) {
        free(m->heap);
        free(m);
    }
}

// –û—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è
int main() {
