    Memory* mem = create_memory(1000);
    if (!mem) {
        perror("Memory allocation failed");
        return 1;
    }

    // –°–æ–∑–¥–∞–Ω–∏–µ –∫—ç—à–∞
    Cache* cache = create_cache(100);
    if (!cache) {
        perror("Cache allocation failed");
        memory_free(mem);
        return 1;
    }

    // –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á
    Task* task1 = create_task(1, task_func, (void*)1);
    Task* task2 = create_task(2, task_func, (void*)2);
    Task* task3 = create_task(3, task_func, (void*)3);
    Task* task4 = create_task(4, task_func, (void*)4);

    // –°–æ–∑–¥–∞–Ω–∏–µ –æ—á–µ—Ä–µ–¥–µ–π
    Queue* q1 = create_queue(5);
    Queue* q2 = create_queue(5);

    // –°–æ–∑–¥–∞–Ω–∏–µ —Å–µ–º–∞—Ñ–æ—Ä–æ–≤
    Semaphore* s1 = create_semaphore(1);
    Semaphore* s2 = create_semaphore(1);

    // –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
    queue_send(q1, 42);
    queue_send(q2, 77);

    semaphore_acquire(s1);
    semaphore_acquire(s2);

    // –ó–∞–ø—É—Å–∫ –æ—Å–Ω–æ–≤–Ω–æ–≥–æ —Ü–∏–∫–ª–∞
    while (1) {
        // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –ª–æ–≥–∏–∫—É –æ–±—Ä–∞–±–æ—Ç–∫–∏ —Å–æ–±—ã—Ç–∏–π
        sleep(1);
    }

    // –û—Å–≤–æ–±–æ–∂–¥–µ–Ω–∏–µ —Ä–µ—Å—É—Ä—Å–æ–≤
    memory_free(mem);
    cache_clear(cache);
    free(cache);
    return 0;
}
```

