    }

    fft(input, output, n);

    // –í—ã–≤–æ–¥ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
    for (int i = 0; i < n; i++) {
        printf("Output[%d] = %.4f + %.4fi\n", i, output[i], (output[i] + 1.0f));
    }

    return 0;
}
