#include "task.h"
#include "scheduler.h"

void task_run(task_func_t func, void *arg, int priority) {
    struct task *t = malloc(sizeof(struct task));
    t->func = func;
    t->arg = arg;
    t->priority = priority;
    t->state = 1;
    t->ticks_left = 1000; // 1 —Å–µ–∫—É–Ω–¥–∞
    t->id = ++task_id;

    // –î–æ–±–∞–≤–ª—è–µ–º –≤ —Å–ø–∏—Å–æ–∫ –∑–∞–¥–∞—á
    // (–≤ —Ä–µ–∞–ª—å–Ω–æ–º —Å–ª—É—á–∞–µ —ç—Ç–æ –±—É–¥–µ—Ç —Å–≤—è–∑–∞–Ω–æ —Å –∫–æ–ª—å—Ü–æ–º –∑–∞–¥–∞—á)
}

void task_suspend(struct task *t) {
    t->state = 0;
}

void task_resume(struct task *t) {
    t->state = 1;
}
```

