## ‚úÖ –ü—Ä–∏–º–µ—Ä –∫–æ–¥–∞: –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ `fib` —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º LLVM-C

```cpp
#include <llvm/IR/LLVMContext.h>
#include <llvm/IR/Module.h>
#include <llvm/IR/Function.h>
#include <llvm/IR/Type.h>
#include <llvm/IR/Instructions.h>
#include <llvm/IR/IRBuilder.h>
#include <llvm/Support/Debug.h>
#include <iostream>

using namespace llvm;

int main() {
    // –°–æ–∑–¥–∞–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç LLVM
    LLVMContext context;
    Module *module = new Module("fib_module", &context);

    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø –≤—Ö–æ–¥–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö (n)
    Type *intType = IntegerType::get(&context, 32);
    Type *funcType = FunctionType::get(intType, {intType}, false);

    // –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é fib
    Function *fibFunc = module->getFunction(funcType);
    if (!fibFunc) {
        fibFunc = Function::Create(funcType, Function::ExternalLinkage, "fib", module);
    }

    // –î–æ–±–∞–≤–ª—è–µ–º –±–ª–æ–∫ –∫–æ–¥–∞
    BasicBlock *entry = BasicBlock::Create(&context, "entry", fibFunc);
    IRBuilder<> builder(&entry->begin());

    // –í—Ö–æ–¥–Ω–æ–π –ø–∞—Ä–∞–º–µ—Ç—Ä
    Value *n = builder->getInt32(0);
    Value *res = builder->getInt32(0);

    // –ë–∞–∑–æ–≤—ã–π —Å–ª—É—á–∞–π: fib(0) = 0
    builder->store(res, builder->getInsertPoint());

    // –†–µ–∫—É—Ä—Ä–µ–Ω—Ç–Ω—ã–π —Å–ª—É—á–∞–π: fib(n) = fib(n-1) + fib(n-2)
    // –ù–∞—á–∏–Ω–∞–µ–º —Å n=1, 2, ...
    for (int i = 1; i <= 32; ++i) {
        if (i == 1) {
            res = builder->getInt32(1);
        } else {
            Value *prev = builder->getInt32(i - 1);
            Value *prev_prev = builder->getInt32(i - 2);
            Value *sum = builder->add(prev, prev_prev);
            res = sum;
        }
        builder->store(res, builder->getInsertPoint());
    }

    // –í—ã—Ö–æ–¥–Ω–∞—è –ø–µ—Ä–µ–º–µ–Ω–Ω–∞—è
    builder->store(res, builder->getInsertPoint());

    // –í—ã–≤–æ–¥ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    Value *result = fibFunc->getFunctionResult();
    builder->store(result, builder->getInsertPoint());

    // –°–æ—Ö—Ä–∞–Ω—è–µ–º –º–æ–¥—É–ª—å
    module->print(errs(), nullptr);

    return 0;
}
