### ‚úÖ –ü—Ä–∏–º–µ—Ä: –≠–º—É–ª—è—Ü–∏—è FreeRTOS-–ø–æ–¥–æ–±–Ω–æ–π –û–° –Ω–∞ C (–¥–ª—è —É—á–µ–±–Ω—ã—Ö —Ü–µ–ª–µ–π)

```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <pthread.h>
#include <unistd.h>
#include <time.h>

// ==================== –ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è ====================
#define configMAX_PRIORITIES          5
#define configMINIMAL_STACK_SIZE      128
#define configTICK_RATE_HZ            100  // 100 —Ç–∏–∫–æ–≤ –≤ —Å–µ–∫—É–Ω–¥—É

// ==================== –°—Ç—Ä—É–∫—Ç—É—Ä—ã –¥–∞–Ω–Ω—ã—Ö ====================

typedef enum {
    eTaskStateReady,
    eTaskStateRunning,
    eTaskStateBlocked,
    eTaskStateSuspended
} eTaskState;

typedef struct TaskHandle_t {
    void (*pxTaskCode)(void *pvParameters);
    void *pvParameters;
    char pcTaskName[16];
    int uxPriority;
    int xStackSize;
    eTaskState eCurrentState;
    pthread_t xTaskHandle;  // –ü–æ—Ç–æ–∫ POSIX
    int xTimeSlice;         // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ç–∏–∫–æ–≤ –¥–æ —Å–ª–µ–¥—É—é—â–µ–≥–æ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è
} TaskHandle_t;

// ==================== –ì–ª–æ–±–∞–ª—å–Ω—ã–µ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ ====================

static TaskHandle_t *pxCurrentTask = NULL;
static TaskHandle_t *pxReadyTasks[configMAX_PRIORITIES][10];  // –ú–∞—Å—Å–∏–≤ –≥–æ—Ç–æ–≤—ã—Ö –∑–∞–¥–∞—á –ø–æ –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–∞–º
static int uxReadyTaskCount[configMAX_PRIORITIES] = {0};
static int xTickCount = 0;
static pthread_mutex_t xSchedulerMutex = PTHREAD_MUTEX_INITIALIZER;

// ==================== –§—É–Ω–∫—Ü–∏–∏ —è–¥—Ä–∞ ====================

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è —Å–∏—Å—Ç–µ–º—ã
void vTaskStartScheduler(void) {
    printf("FreeRTOS-like scheduler started.\n");
    printf("Tick rate: %d Hz\n", configTICK_RATE_HZ);

    // –ó–∞–ø—É—Å–∫ —Ç–∞–π–º–µ—Ä–∞ (–≤ –æ—Ç–¥–µ–ª—å–Ω–æ–º –ø–æ—Ç–æ–∫–µ)
    pthread_t timer_thread;
    pthread_create(&timer_thread, NULL, vTimerTask, NULL);
    pthread_detach(timer_thread);
}

// –¢–∞–π–º–µ—Ä (—Å–º–æ–¥–µ–ª–∏—Ä–æ–≤–∞–Ω–Ω—ã–π)
void* vTimerTask(void *pvParameters) {
    while (1) {
        usleep(1000000 / configTICK_RATE_HZ);  // –ü–∞—É–∑–∞ –º–µ–∂–¥—É —Ç–∏–∫–∞–º–∏
        pthread_mutex_lock(&xSchedulerMutex);
        xTickCount++;
        // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ (–ø—Ä–æ—Å—Ç–æ –≤—ã–∑–æ–≤)
        vTaskSwitchContext();
        pthread_mutex_unlock(&xSchedulerMutex);
    }
    return NULL;
}

// –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ (–≤—ã–±–æ—Ä —Å–ª–µ–¥—É—é—â–µ–π –∑–∞–¥–∞—á–∏)
void vTaskSwitchContext(void) {
    if (pxCurrentTask == NULL) return;

    // –£–º–µ–Ω—å—à–∞–µ–º –≤—Ä–µ–º—è –∫–≤–∞–Ω—Ç–∞
    pxCurrentTask->xTimeSlice--;

    // –ï—Å–ª–∏ –∫–≤–∞–Ω—Ç –∑–∞–∫–æ–Ω—á–∏–ª—Å—è ‚Äî –ø–µ—Ä–µ–∫–ª—é—á–∞–µ–º—Å—è
    if (pxCurrentTask->xTimeSlice <= 0) {
        // –ù–∞–π—Ç–∏ —Å–ª–µ–¥—É—é—â—É—é –∑–∞–¥–∞—á—É —Å –≤—ã—Å—à–∏–º –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–æ–º
        for (int prio = configMAX_PRIORITIES - 1; prio >= 0; prio--) {
            if (uxReadyTaskCount[prio] > 0) {
                TaskHandle_t *next_task = pxReadyTasks[prio][0];
                // –£–¥–∞–ª—è–µ–º –∏–∑ —Å–ø–∏—Å–∫–∞
                memmove(pxReadyTasks[prio], pxReadyTasks[prio] + 1, 
                        sizeof(TaskHandle_t*) * (uxReadyTaskCount[prio] - 1));
                uxReadyTaskCount[prio]--;

                // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ
                pxCurrentTask = next_task;
                pxCurrentTask->xTimeSlice = 10;  // –ù–æ–≤—ã–π –∫–≤–∞–Ω—Ç
                printf("Task switched to: %s (priority %d)\n", 
                       pxCurrentTask->pcTaskName, pxCurrentTask->uxPriority);
                break;
            }
        }
    }
}

// –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á–∏
TaskHandle_t* xTaskCreate(
    void (*pxTaskCode)(void *pvParameters),
    const char *pcName,
    uint32_t usStackDepth,
    void *pvParameters,
    UBaseType_t uxPriority,
    TaskHandle_t *pxCreatedTask
) {
    TaskHandle_t *pxNewTask = (TaskHandle_t*)malloc(sizeof(TaskHandle_t));
    if (!pxNewTask) return NULL;

    pxNewTask->pxTaskCode = pxTaskCode;
    pxNewTask->pvParameters = pvParameters;
    strncpy(pxNewTask->pcTaskName, pcName, 15);
    pxNewTask->pcTaskName[15] = '\0';
    pxNewTask->uxPriority = uxPriority;
    pxNewTask->xStackSize = usStackDepth;
    pxNewTask->eCurrentState = eTaskStateReady;
    pxNewTask->xTimeSlice = 10;  // –ù–∞—á–∞–ª—å–Ω—ã–π –∫–≤–∞–Ω—Ç

    // –î–æ–±–∞–≤–ª—è–µ–º –≤ —Å–ø–∏—Å–æ–∫ –≥–æ—Ç–æ–≤—ã—Ö –∑–∞–¥–∞—á
    pthread_mutex_lock(&xSchedulerMutex);
    if (uxReadyTaskCount[uxPriority] < 10) {
        pxReadyTasks[uxPriority][uxReadyTaskCount[uxPriority]] = pxNewTask;
        uxReadyTaskCount[uxPriority]++;
    }
    pthread_mutex_unlock(&xSchedulerMutex);

    if (pxCreatedTask) *pxCreatedTask = pxNewTask;

    // –ó–∞–ø—É—Å–∫–∞–µ–º –∑–∞–¥–∞—á—É –≤ –æ—Ç–¥–µ–ª—å–Ω–æ–º –ø–æ—Ç–æ–∫–µ
    pthread_create(&pxNewTask->xTaskHandle, NULL, vTaskWrapper, (void*)pxNewTask);
    pthread_detach(pxNewTask->xTaskHandle);

    return pxNewTask;
}

// –û–±—ë—Ä—Ç–∫–∞ –¥–ª—è –∑–∞–ø—É—Å–∫–∞ –∑–∞–¥–∞—á–∏ –≤ –ø–æ—Ç–æ–∫–µ
void* vTaskWrapper(void *pvParameters) {
    TaskHandle_t *pxTask = (TaskHandle_t*)pvParameters;

    // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Ç–µ–∫—É—â—É—é –∑–∞–¥–∞—á—É
    pthread_mutex_lock(&xSchedulerMutex);
    pxCurrentTask = pxTask;
    pxTask->eCurrentState = eTaskStateRunning;
    pthread_mutex_unlock(&xSchedulerMutex);

    // –í—ã–ø–æ–ª–Ω—è–µ–º –∑–∞–¥–∞—á—É
    pxTask->pxTaskCode(pxTask->pvParameters);

    // –ó–∞–¥–∞—á–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞
    pthread_mutex_lock(&xSchedulerMutex);
    pxTask->eCurrentState = eTaskStateBlocked;
    pthread_mutex_unlock(&xSchedulerMutex);

    pthread_exit(NULL);
}

// –ó–∞–¥–µ—Ä–∂–∫–∞ (–≤ —Ç–∏–∫–∞—Ö)
void vTaskDelay(uint32_t xTicksToDelay) {
    uint32_t start_tick = xTickCount;
    while ((xTickCount - start_tick) < xTicksToDelay) {
        usleep(10000);  // –ù–µ–±–æ–ª—å—à–∞—è –∑–∞–¥–µ—Ä–∂–∫–∞, —á—Ç–æ–±—ã –Ω–µ –∂—Ä–∞—Ç—å CPU
    }
}

// ==================== –ü—Ä–∏–º–µ—Ä—ã –∑–∞–¥–∞—á ====================

void vTaskLED(void *pvParameters) {
    int led_state = 0;
    while (1) {
        printf("LED: %s\n", led_state ? "ON" : "OFF");
        led_state = !led_state;
        vTaskDelay(5);  // –ó–∞–¥–µ—Ä–∂–∫–∞ 5 —Ç–∏–∫–æ–≤ (0.05 —Å–µ–∫)
    }
}

void vTaskSensor(void *pvParameters) {
    while (1) {
        printf("Sensor reading: %d\n", (int)(clock() % 100));
        vTaskDelay(3);  // –ö–∞–∂–¥—ã–µ 3 —Ç–∏–∫–∞
    }
}

void vTaskMonitor(void *pvParameters) {
    while (1) {
        printf("System tick: %d\n", xTickCount);
        vTaskDelay(10);  // –ö–∞–∂–¥—ã–µ 10 —Ç–∏–∫–æ–≤
    }
}

// ==================== –ì–ª–∞–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è ====================

int main(void) {
    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è —Å–∏—Å—Ç–µ–º—ã
    vTaskStartScheduler();

    // –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á
    TaskHandle_t *xLEDTask, *xSensorTask, *xMonitorTask;

    xTaskCreate(vTaskLED, "LED_Task", configMINIMAL_STACK_SIZE, NULL, 2, &xLEDTask);
    xTaskCreate(vTaskSensor, "Sensor_Task", configMINIMAL_STACK_SIZE, NULL, 1, &xSensorTask);
    xTaskCreate(vTaskMonitor, "Monitor_Task", configMINIMAL_STACK_SIZE, NULL, 3, &xMonitorTask);

    // –û–∂–∏–¥–∞–Ω–∏–µ (–±–µ—Å–∫–æ–Ω–µ—á–Ω—ã–π —Ü–∏–∫–ª)
    while (1) {
        sleep(1);
    }

    return 0;
}
