```c
// fft_hls.cpp
#include "fft_hls.h"
#include <math.h>

// –ü—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ –≤—ã—á–∏—Å–ª–µ–Ω–Ω—ã–µ –∫–æ—ç—Ñ—Ñ–∏—Ü–∏–µ–Ω—Ç—ã W_N^k (–¥–ª—è —É—Å–∫–æ—Ä–µ–Ω–∏—è)
// –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ç–∞–±–ª–∏—Ü—É, —á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –≤—ã—á–∏—Å–ª–µ–Ω–∏–π –≤ —Ä–µ–∞–ª—å–Ω–æ–º –≤—Ä–µ–º–µ–Ω–∏
const complex_t w_table[N_FFT / 2] = []() {
    complex_t table[N_FFT / 2];
    for (int k = 0; k < N_FFT / 2; ++k) {
        double angle = -2.0 * M_PI * k / N_FFT;
        table[k].real = cos(angle);
        table[k].imag = sin(angle);
    }
    return table;
}();

// –í—ã—á–∏—Å–ª–µ–Ω–∏–µ W_N^k
complex_t compute_w(int k, int N) {
    return w_table[k % (N / 2)];
}

// –ë–∏—Ç–æ–≤–æ–µ –ø–µ—Ä–µ–≤–æ—Ä–∞—á–∏–≤–∞–Ω–∏–µ
int bit_reverse(int x, int log_n) {
    int rev = 0;
    for (int i = 0; i < log_n; ++i) {
        rev = (rev << 1) | (x & 1);
        x >>= 1;
    }
    return rev;
}

// –£–º–Ω–æ–∂–µ–Ω–∏–µ –∫–æ–º–ø–ª–µ–∫—Å–Ω—ã—Ö —á–∏—Å–µ–ª
complex_t cmul(const complex_t& a, const complex_t& b) {
    complex_t c;
    c.real = a.real * b.real - a.imag * b.imag;
    c.imag = a.real * b.imag + a.imag * b.real;
    return c;
}

// –°–ª–æ–∂–µ–Ω–∏–µ –∫–æ–º–ø–ª–µ–∫—Å–Ω—ã—Ö —á–∏—Å–µ–ª
complex_t cadd(const complex_t& a, const complex_t& b) {
    complex_t c;
    c.real = a.real + b.real;
    c.imag = a.imag + b.imag;
    return c;
}

// –í—ã—á–∏—Ç–∞–Ω–∏–µ –∫–æ–º–ø–ª–µ–∫—Å–Ω—ã—Ö —á–∏—Å–µ–ª
complex_t csub(const complex_t& a, const complex_t& b) {
    complex_t c;
    c.real = a.real - b.real;
    c.imag = a.imag - b.imag;
    return c;
}

// –û—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è FFT
void fft_1024(hls::stream<complex_t>& in_stream, hls::stream<complex_t>& out_stream) {
    // –ë—É—Ñ–µ—Ä –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö
    complex_t buffer[N_FFT];

    // –ß—Ç–µ–Ω–∏–µ –≤—Ö–æ–¥–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö
    #pragma HLS PIPELINE II=1
    for (int i = 0; i < N_FFT; ++i) {
        complex_t tmp;
        in_stream >> tmp;
        buffer[i] = tmp;
    }

    // –ë–∏—Ç–æ–≤–æ–µ –ø–µ—Ä–µ–≤–æ—Ä–∞—á–∏–≤–∞–Ω–∏–µ
    complex_t temp_buffer[N_FFT];
    #pragma HLS PIPELINE II=1
    for (int i = 0; i < N_FFT; ++i) {
        int br = bit_reverse(i, LOG_N_FFT);
        temp_buffer[br] = buffer[i];
    }

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω–∞—è FFT (–≤ –≤–∏–¥–µ –∏—Ç–µ—Ä–∞—Ü–∏–π –ø–æ —É—Ä–æ–≤–Ω—è–º)
    int stage = 1;
    int step = 2;
    int half_step = 1;

    // –ò—Ç–µ—Ä–∞—Ü–∏–∏ –ø–æ —É—Ä–æ–≤–Ω—è–º
    for (int s = 0; s < LOG_N_FFT; ++s) {
        #pragma HLS PIPELINE II=1
        for (int i = 0; i < N_FFT; i += step) {
            #pragma HLS UNROLL factor=2
            for (int j = 0; j < half_step; ++j) {
                int k = i + j;
                int l = k + half_step;

                complex_t w = compute_w(j * (N_FFT / step), N_FFT);
                complex_t t = cmul(w, temp_buffer[l]);
                complex_t u = temp_buffer[k];

                temp_buffer[k] = cadd(u, t);
                temp_buffer[l] = csub(u, t);
            }
        }
        step <<= 1;
        half_step <<= 1;
    }

    // –ó–∞–ø–∏—Å—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    #pragma HLS PIPELINE II=1
    for (int i = 0; i < N_FFT; ++i) {
        out_stream << temp_buffer[i];
    }
}
