## ‚úÖ –ö–æ–¥ –Ω–∞ C —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º `llvm-c`

```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// –ü–æ–¥–∫–ª—é—á–∞–µ–º LLVM C API
#include "llvm-c/Core.h"
#include "llvm-c/ExecutionEngine.h"
#include "llvm-c/Target.h"
#include "llvm-c/Transforms/PassManagerBuilder.h"

// –ú–∞–∫—Ä–æ—Å –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ –æ—à–∏–±–æ–∫
#define CHECK_ERROR(expr, msg) \
    do { \
        if (!(expr)) { \
            fprintf(stderr, "Error: %s\n", msg); \
            exit(1); \
        } \
    } while(0)

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ fib(n)
void generate_fib_function(LLVMModuleRef module) {
    // 1. –°–æ–∑–¥–∞—ë–º –∫–æ–Ω—Ç–µ–∫—Å—Ç (–µ—Å–ª–∏ –µ—â—ë –Ω–µ —Å–æ–∑–¥–∞–Ω)
    LLVMContextRef context = LLVMGetGlobalContext();

    // 2. –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø—ã
    LLVMTypeRef i32_type = LLVMInt32TypeInContext(context);
    LLVMTypeRef param_types[] = { i32_type };
    LLVMTypeRef func_type = LLVMFunctionType(i32_type, param_types, 1, 0);

    // 3. –°–æ–∑–¥–∞—ë–º —Ñ—É–Ω–∫—Ü–∏—é fib
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", func_type);
    CHECK_ERROR(fib_func != NULL, "Failed to create function 'fib'");

    // 4. –°–æ–∑–¥–∞—ë–º –±–ª–æ–∫ –≤—Ö–æ–¥–∞
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlock(fib_func, "entry");
    LLVMBuilderRef builder = LLVMCreateBuilderInContext(context);
    LLVMPositionBuilderAtEnd(builder, entry_block);

    // 5. –ü–æ–ª—É—á–∞–µ–º –ø–∞—Ä–∞–º–µ—Ç—Ä n
    LLVMValueRef n = LLVMGetParam(fib_func, 0);

    // 6. –°–æ–∑–¥–∞—ë–º –∫–æ–Ω—Å—Ç–∞–Ω—Ç—ã
    LLVMValueRef zero = LLVMConstInt(i32_type, 0, 0);
    LLVMValueRef one = LLVMConstInt(i32_type, 1, 0);
    LLVMValueRef two = LLVMConstInt(i32_type, 2, 0);

    // 7. –ü—Ä–æ–≤–µ—Ä–∫–∞: –µ—Å–ª–∏ n <= 1, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º n
    LLVMValueRef cond = LLVMBuildICmp(builder, LLVMIntULE, n, one, "n_le_1");
    LLVMBasicBlockRef then_block = LLVMAppendBasicBlock(fib_func, "then");
    LLVMBasicBlockRef else_block = LLVMAppendBasicBlock(fib_func, "else");
    LLVMBasicBlockRef merge_block = LLVMAppendBasicBlock(fib_func, "merge");

    LLVMBuildCondBr(builder, cond, then_block, else_block);

    // 8. –ë–ª–æ–∫ then: return n
    LLVMPositionBuilderAtEnd(builder, then_block);
    LLVMBuildRet(builder, n);
    LLVMPositionBuilderAtEnd(builder, else_block);

    // 9. –ò—Ç–µ—Ä–∞—Ç–∏–≤–Ω—ã–π —Ü–∏–∫–ª: a, b = 0, 1; for i = 2 to n
    // –°–æ–∑–¥–∞—ë–º –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ
    LLVMValueRef a = LLVMBuildAlloca(builder, i32_type, "a");
    LLVMValueRef b = LLVMBuildAlloca(builder, i32_type, "b");
    LLVMValueRef i = LLVMBuildAlloca(builder, i32_type, "i");

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è: a = 0, b = 1
    LLVMBuildStore(builder, zero, a);
    LLVMBuildStore(builder, one, b);

    // i = 2
    LLVMBuildStore(builder, two, i);

    // –°–æ–∑–¥–∞—ë–º —Ü–∏–∫–ª: while (i <= n)
    LLVMBasicBlockRef loop_header = LLVMAppendBasicBlock(fib_func, "loop_header");
    LLVMBasicBlockRef loop_body = LLVMAppendBasicBlock(fib_func, "loop_body");
    LLVMBasicBlockRef loop_end = LLVMAppendBasicBlock(fib_func, "loop_end");

    // –ü–µ—Ä–µ–π—Ç–∏ –∫ –∑–∞–≥–æ–ª–æ–≤–∫—É —Ü–∏–∫–ª–∞
    LLVMBuildBr(builder, loop_header);

    // –ó–∞–≥–æ–ª–æ–≤–æ–∫ —Ü–∏–∫–ª–∞: –ø—Ä–æ–≤–µ—Ä–∫–∞ —É—Å–ª–æ–≤–∏—è
    LLVMPositionBuilderAtEnd(builder, loop_header);
    LLVMValueRef i_val = LLVMBuildLoad(builder, i, "i_val");
    LLVMValueRef loop_cond = LLVMBuildICmp(builder, LLVMIntULE, i_val, n, "loop_cond");
    LLVMBuildCondBr(builder, loop_cond, loop_body, loop_end);

    // –¢–µ–ª–æ —Ü–∏–∫–ª–∞
    LLVMPositionBuilderAtEnd(builder, loop_body);
    LLVMValueRef a_val = LLVMBuildLoad(builder, a, "a_val");
    LLVMValueRef b_val = LLVMBuildLoad(builder, b, "b_val");
    LLVMValueRef next_b = LLVMBuildAdd(builder, a_val, b_val, "next_b");

    // –û–±–Ω–æ–≤–ª—è–µ–º a = b, b = next_b
    LLVMBuildStore(builder, b_val, a);
    LLVMBuildStore(builder, next_b, b);

    // i++
    LLVMValueRef new_i = LLVMBuildAdd(builder, i_val, one, "new_i");
    LLVMBuildStore(builder, new_i, i);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º—Å—è –∫ –∑–∞–≥–æ–ª–æ–≤–∫—É
    LLVMBuildBr(builder, loop_header);

