  %b_val = load i32, i32* %b
  %next_b = add i32 %a_val, %b_val
  store i32 %b_val, i32* %a
  store i32 %next_b, i32* %b
  %new_i = add i32 %i_val, 1
  store i32 %new_i, i32* %i
  br label %loop_header

loop_end:
