```java
public class Fraction {
    private int numerator;
    private int denominator;

    // –ö–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä
    public Fraction(int num, int den) {
        if (den == 0) {
            throw new IllegalArgumentException("–ó–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –Ω—É–ª—ë–º");
        }
        if (den < 0) {
            num = -num;
            den = -den;
        }
        this.numerator = num;
        this.denominator = den;
        simplify();
    }

    // –£–ø—Ä–æ—â–µ–Ω–∏–µ –¥—Ä–æ–±–∏
    private void simplify() {
        int gcd = gcd(Math.abs(numerator), Math.abs(denominator));
        numerator /= gcd;
        denominator /= gcd;
    }

    // –ù–û–î (–Ω–∞–∏–±–æ–ª—å—à–∏–π –æ–±—â–∏–π –¥–µ–ª–∏—Ç–µ–ª—å)
    private int gcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }

    // –°–ª–æ–∂–µ–Ω–∏–µ
    public Fraction add(Fraction other) {
        int newNum = this.numerator * other.denominator + other.numerator * this.denominator;
        int newDen = this.denominator * other.denominator;
        return new Fraction(newNum, newDen);
    }

    // –í—ã—á–∏—Ç–∞–Ω–∏–µ
    public Fraction subtract(Fraction other) {
        int newNum = this.numerator * other.denominator - other.numerator * this.denominator;
        int newDen = this.denominator * other.denominator;
        return new Fraction(newNum, newDen);
    }

    // –£–º–Ω–æ–∂–µ–Ω–∏–µ
    public Fraction multiply(Fraction other) {
        int newNum = this.numerator * other.numerator;
        int newDen = this.denominator * other.denominator;
        return new Fraction(newNum, newDen);
    }

    // –î–µ–ª–µ–Ω–∏–µ
    public Fraction divide(Fraction other) {
        if (other.numerator == 0) {
            throw new ArithmeticException("–î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å");
        }
        int newNum = this.numerator * other.denominator;
        int newDen = this.denominator * other.numerator;
        return new Fraction(newNum, newDen);
    }

    // –í—ã–≤–æ–¥ –¥—Ä–æ–±–∏ –≤ –≤–∏–¥–µ "a/b" –∏–ª–∏ "a" –µ—Å–ª–∏ –∑–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å 1
    @Override
    public String toString() {
        if (denominator == 1) {
            return String.valueOf(numerator);
        }
        return numerator + "/" + denominator;
    }

    // –ì–µ—Ç—Ç–µ—Ä—ã
    public int getNumerator() { return numerator; }
    public int getDenominator() { return denominator; }
}
