```java
import java.util.Scanner;

public class Calculator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.println("=== –ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –¥—Ä–æ–±–µ–π ===");
        System.out.println("–í–≤–µ–¥–∏—Ç–µ –ø–µ—Ä–≤—É—é –¥—Ä–æ–±—å –≤ —Ñ–æ—Ä–º–∞—Ç–µ: a/b");
        String input1 = scanner.nextLine().trim();
        Fraction f1 = parseFraction(input1);

        System.out.println("–í–≤–µ–¥–∏—Ç–µ –≤—Ç–æ—Ä—É—é –¥—Ä–æ–±—å –≤ —Ñ–æ—Ä–º–∞—Ç–µ: a/b");
        String input2 = scanner.nextLine().trim();
        Fraction f2 = parseFraction(input2);

        System.out.println("\n–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é:");
        System.out.println("1. –°–ª–æ–∂–µ–Ω–∏–µ (+)");
        System.out.println("2. –í—ã—á–∏—Ç–∞–Ω–∏–µ (-)");
        System.out.println("3. –£–º–Ω–æ–∂–µ–Ω–∏–µ (*)");
        System.out.println("4. –î–µ–ª–µ–Ω–∏–µ (/)");

        int choice = scanner.nextInt();

        Fraction result = null;
        String operation = "";

        switch (choice) {
            case 1:
                result = f1.add(f2);
                operation = "+";
                break;
            case 2:
                result = f1.subtract(f2);
                operation = "-";
                break;
            case 3:
                result = f1.multiply(f2);
                operation = "*";
                break;
            case 4:
                result = f1.divide(f2);
                operation = "/";
                break;
            default:
                System.out.println("–ù–µ–≤–µ—Ä–Ω—ã–π –≤—ã–±–æ—Ä!");
                scanner.close();
                return;
        }

        System.out.println("\n–†–µ–∑—É–ª—å—Ç–∞—Ç: " + f1 + " " + operation + " " + f2 + " = " + result);

        scanner.close();
    }

    // –ü–∞—Ä—Å–∏–Ω–≥ —Å—Ç—Ä–æ–∫–∏ –≤–∏–¥–∞ "a/b" –∏–ª–∏ "a"
    private static Fraction parseFraction(String input) {
        String[] parts = input.split("/");
        if (parts.length == 1) {
            try {
                int num = Integer.parseInt(parts[0]);
                return new Fraction(num, 1);
            } catch (NumberFormatException e) {
                throw new IllegalArgumentException("–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏: " + input);
            }
        } else if (parts.length == 2) {
            try {
                int num = Integer.parseInt(parts[0]);
                int den = Integer.parseInt(parts[1]);
                return new Fraction(num, den);
            } catch (NumberFormatException e) {
                throw new IllegalArgumentException("–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏: " + input);
            }
        } else {
            throw new IllegalArgumentException("–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏: " + input);
        }
    }
}
