        let handle = thread::spawn(move || {
            // –ë–µ—Ä—ë–º –±–ª–æ–∫–∏—Ä–æ–≤–∫—É (–º—å—é—Ç–µ–∫—Å) –∏ –∏–∑–º–µ–Ω—è–µ–º —Å—Ç—Ä–æ–∫—É
            let mut s = string_clone.lock().unwrap();
            s.push_str(&format!(" –∏–∑ –ø–æ—Ç–æ–∫–∞ {}", i));
            println!("–ü–æ—Ç–æ–∫ {}: {}", i, s);
