```python
#!/usr/bin/env python3

import sys
import fileinput

def read_lines(filename):
    with open(filename, 'r') as f:
        return [line.strip() for line in f if line.strip()]

def main():
    if len(sys.argv) != 4:
        print("Usage: custom_checker.py input output answer", file=sys.stderr)
        sys.exit(1)

    input_file = sys.argv[1]
    output_file = sys.argv[2]
    answer_file = sys.argv[3]

    # –ß–∏—Ç–∞–µ–º –≤—Å–µ —Ç—Ä–∏ —Ñ–∞–π–ª–∞
    try:
        input_lines = read_lines(input_file)
        output_lines = read_lines(output_file)
        answer_lines = read_lines(answer_file)
    except Exception as e:
        print(f"Error reading files: {e}", file=sys.stderr)
        sys.exit(1)

    # –ü—Ä–∏–º–µ—Ä: —Å—Ä–∞–≤–Ω–∏–≤–∞–µ–º, —á—Ç–æ –≤—ã—Ö–æ–¥–Ω—ã–µ —Å—Ç—Ä–æ–∫–∏ —Å–æ–¥–µ—Ä–∂–∞—Ç —Ç–µ –∂–µ —á–∏—Å–ª–∞, –Ω–æ –≤ –ª—é–±–æ–º –ø–æ—Ä—è–¥–∫–µ
    # (–Ω–∞–ø—Ä–∏–º–µ—Ä, –¥–ª—è –∑–∞–¥–∞—á–∏ "—Å—É–º–º–∞ –¥–≤—É—Ö —á–∏—Å–µ–ª", –≥–¥–µ –ø–æ—Ä—è–¥–æ–∫ –Ω–µ –≤–∞–∂–µ–Ω)

    # –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º —Å—Ç—Ä–æ–∫–∏ –≤ –º–Ω–æ–∂–µ—Å—Ç–≤–∞ —á–∏—Å–µ–ª
    def extract_numbers(lines):
        numbers = set()
        for line in lines:
            try:
                num = int(line)
                numbers.add(num)
            except ValueError:
                pass  # –ø—Ä–æ–ø—É—Å–∫–∞–µ–º –Ω–µ—á–∏—Å–ª–æ–≤—ã–µ —Å—Ç—Ä–æ–∫–∏
        return numbers

    output_nums = extract_numbers(output_lines)
    answer_nums = extract_numbers(answer_lines)

    # –°—Ä–∞–≤–Ω–µ–Ω–∏–µ: –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å –æ–¥–∏–Ω–∞–∫–æ–≤—ã–º–∏
    if output_nums == answer_nums:
        print("AC")
    else:
        print("WA")

if __name__ == "__main__":
    main()
```

