pthread_cond_t task_cond = PTHREAD_COND_INITIALIZER;
int running = 1;

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –∑–∞–¥–∞—á–∏ –≤ —Å–ø–∏—Å–æ–∫
void add_task(void (*func)(void*), void* arg, int priority) {
    Task* new_task = (Task*)malloc(sizeof(Task));
    new_task->task_func = func;
    new_task->arg = arg;
    new_task->priority = priority;
    new_task->next = NULL;

    pthread_mutex_lock(&task_mutex);
    if (!task_list || priority < task_list->priority) {
        new_task->next = task_list;
        task_list = new_task;
    } else {
        Task* current = task_list;
        while (current->next && current->next->priority <= priority) {
            current = current->next;
        }
        new_task->next = current->next;
        current->next = new_task;
    }
    pthread_mutex_unlock(&task_mutex);
    pthread_cond_signal(&task_cond);
}

// –§—É–Ω–∫—Ü–∏—è –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫–∞ –∑–∞–¥–∞—á
void* scheduler(void* arg) {
    while (running) {
        pthread_mutex_lock(&task_mutex);
        if (!task_list) {
            pthread_cond_wait(&task_cond, &task_mutex);
        }

        Task* current = task_list;
        if (current) {
            task_list = current->next;
            pthread_mutex_unlock(&task_mutex);

            current->task_func(current->arg);
            free(current);
        } else {
            pthread_mutex_unlock(&task_mutex);
        }

        usleep(100000); // –ò–º–∏—Ç–∞—Ü–∏—è –∑–∞–¥–µ—Ä–∂–∫–∏
    }
    return NULL;
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á–∏: –≤—ã–≤–æ–¥ —Å–æ–æ–±—â–µ–Ω–∏—è
void task1(void* arg) {
    printf("–ó–∞–¥–∞—á–∞ 1: –ü—Ä–∏–≤–µ—Ç –∏–∑ –∑–∞–¥–∞—á–∏ 1!\n");
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á–∏: –≤—ã–≤–æ–¥ —Å–æ–æ–±—â–µ–Ω–∏—è
void task2(void* arg) {
    printf("–ó–∞–¥–∞—á–∞ 2: –ü—Ä–∏–≤–µ—Ç –∏–∑ –∑–∞–¥–∞—á–∏ 2!\n");
}

// –û—Å–Ω–æ–≤–Ω–∞—è –ø—Ä–æ–≥—Ä–∞–º–º–∞
int main() {
    // –°–æ–∑–¥–∞–Ω–∏–µ –ø–æ—Ç–æ–∫–∞ –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫–∞
    pthread_t scheduler_thread;
    pthread_create(&scheduler_thread, NULL, scheduler, NULL);

    // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –∑–∞–¥–∞—á –≤ –æ—á–µ—Ä–µ–¥—å
    add_task(task1, NULL, 1);
    add_task(task2, NULL, 2);

    // –ó–∞–ø—É—Å–∫ –∑–∞–¥–∞—á
    sleep(1); // –ñ–¥—ë–º, –ø–æ–∫–∞ –∑–∞–¥–∞—á–∏ –≤—ã–ø–æ–ª–Ω—è—Ç—Å—è

    // –û—Å—Ç–∞–Ω–æ–≤–∫–∞ –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫–∞
    running = 0;
    pthread_cond_signal(&task_cond);
    pthread_join(scheduler_thread, NULL);

    return 0;
}
