LLVMValueRef create_fib_function(LLVMModuleRef module) {
    // 1. –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–æ–≤
    LLVMTypeRef int32_type = LLVMInt32Type();
    LLVMTypeRef func_type = LLVMFunctionType(int32_type, &int32_type, 1, 0);

    // 2. –û–±—ä—è–≤–ª–µ–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", func_type);

    // 3. –°–æ–∑–¥–∞–Ω–∏–µ –±–ª–æ–∫–æ–≤
    LLVMValueRef entry_block = LLVMAppendBasicBlock(fib_func, "entry");
    LLVMValueRef base_case_block = LLVMAppendBasicBlock(fib_func, "base_case");
    LLVMValueRef recurse1_block = LLVMAppendBasicBlock(fib_func, "recurse1");
    LLVMValueRef recurse2_block = LLVMAppendBasicBlock(fib_func, "recurse2");
    LLVMValueRef add_block = LLVMAppendBasicBlock(fib_func, "add");
    LLVMValueRef return_block = LLVMAppendBasicBlock(fib_func, "return");

    // 4. –ù–∞—Å—Ç—Ä–æ–π–∫–∞ builder
    LLVMBasicBlockRef current_block = entry_block;
    LLVMBuilderRef builder = LLVMCreateBuilder();
    LLVMPositionBuilderAtEnd(builder, current_block);

    // 5. –õ–æ–≥–∏–∫–∞ —Ñ—É–Ω–∫—Ü–∏–∏
    // –ó–∞–≥—Ä—É–∑–∫–∞ –∞—Ä–≥—É–º–µ–Ω—Ç–∞ n
    LLVMValueRef n = LLVMGetParam(fib_func, 0);
    LLVMValueRef n_ptr = LLVMMakeValueFromName(fib_func, "n");
    LLVMSetValueName(n, "n");

    // –ü—Ä–æ–≤–µ—Ä–∫–∞: if n <= 1
    LLVMValueRef cmp = LLVMICmp(builder, LLVMIntSLT, n, LLVMConstInt(int32_type, 1, 0));
    LLVMBr(builder, base_case_block, cmp);

    // –ë–∞–∑–æ–≤—ã–π —Å–ª—É—á–∞–π: return n
    LLVMPositionBuilderAtEnd(builder, base_case_block);
    LLVMBuildRet(builder, n);

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π –≤—ã–∑–æ–≤: fib(n-1)
    LLVMPositionBuilderAtEnd(builder, recurse1_block);
    LLVMValueRef n_minus_1 = LLVMBuildSub(builder, n, LLVMConstInt(int32_type, 1, 0), "n-1");
    LLVMValueRef call1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "call1");

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π –≤—ã–∑–æ–≤: fib(n-2)
    LLVMPositionBuilderAtEnd(builder, recurse2_block);
    LLVMValueRef n_minus_2 = LLVMBuildSub(builder, n, LLVMConstInt(int32_type, 2, 0), "n-2");
    LLVMValueRef call2 = LLVMBuildCall(builder, fib_func, &n_minus_2, 1, "call2");

    // –°–ª–æ–∂–µ–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
    LLVMPositionBuilderAtEnd(builder, add_block);
    LLVMValueRef sum = LLVMBuildAdd(builder, call1, call2, "sum");

    // –í–æ–∑–≤—Ä–∞—Ç —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    LLVMPositionBuilderAtEnd(builder, return_block);
    LLVMBuildRet(builder, sum);

    LLVMDisposeBuilder(builder);
    return fib_func;
}

