```java
import java.util.Scanner;

public class Calculator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("–í–≤–µ–¥–∏—Ç–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ –≤ —Ñ–æ—Ä–º–∞—Ç–µ 'a/b op c/d':");
        String input = scanner.nextLine();

        String[] parts = input.split(" ");
        if (parts.length != 3) {
            System.out.println("–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –≤–≤–æ–¥–∞. –ü—Ä–∏–º–µ—Ä: 1/2 + 3/4");
            return;
        }

        String[] first = parts[0].split("/");
        String[] second = parts[2].split("/");

        if (first.length != 2 || second.length != 2) {
            System.out.println("–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏.");
            return;
        }

        try {
            int num1 = Integer.parseInt(first[0]);
            int den1 = Integer.parseInt(first[1]);
            int num2 = Integer.parseInt(second[0]);
            int den2 = Integer.parseInt(second[1]);

            Fraction f1 = new Fraction(num1, den1);
            Fraction f2 = new Fraction(num2, den2);

            Fraction result;
            switch (parts[1]) {
                case "+":
                    result = f1.add(f2);
                    break;
                case "-":
                    result = f1.subtract(f2);
                    break;
                case "*":
                    result = f1.multiply(f2);
                    break;
                case "/":
                    result = f1.divide(f2);
                    break;
                default:
                    System.out.println("–ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π –æ–ø–µ—Ä–∞—Ç–æ—Ä.");
                    return;
            }

            System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç: " + result);
        } catch (NumberFormatException e) {
            System.out.println("–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç —á–∏—Å–ª–∞.");
        } catch (IllegalArgumentException e) {
            System.out.println("–û—à–∏–±–∫–∞: " + e.getMessage());
        }
    }
}
