Task* create_task(void (*func)(void*), void* arg, unsigned int stack_size) {
    Task* task = (Task*)malloc(sizeof(Task));
    if (!task) return NULL;

    task->task_func = func;
    task->arg = arg;
    task->stack_size = stack_size;
    task->stack = (unsigned int*)malloc(stack_size * sizeof(unsigned int));
    if (!task->stack) {
