    printf("Switching from task %p to %p\n", from, to);
}

// –ü–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫ –∑–∞–¥–∞—á
void scheduler() {
    while (1) {
        if (!current_task) {
            current_task = task_list;
        } else {
            current_task = current_task->next;
        }

        if (!current_task) {
            current_task = task_list;
        }

        printf("Running task: %p\n", current_task);
        current_task->task_func(current_task->arg);
    }
}

