        for (volatile int i = 0; i < 1000000; i++);
    }
}

void task2(void* arg) {
    while (1) {
        printf("Task 2 running...\n");
        for (volatile int i = 0; i < 1000000; i++);
    }
}

// –¢–æ—á–∫–∞ –≤—Ö–æ–¥–∞
int main() {
    // –°–æ–∑–¥–∞–µ–º –¥–≤–µ –∑–∞–¥–∞—á–∏
    create_task(task1, NULL, 1024);
    create_task(task2, NULL, 1024);

    // –ó–∞–ø—É—Å–∫–∞–µ–º –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫
    scheduler();

    return 0;
}
