## üìú –ö–æ–¥ –Ω–∞ C —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º llvm-c

```c
#include <stdio.h>
#include <llvm-c/Core.h>
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/Transforms/Scalar.h>

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ fib
void create_fib_function(LLVMModuleRef module) {
    // –°–æ–∑–¥–∞–µ–º —Ç–∏–ø int32_t
    LLVMTypeRef i32 = LLVMInt32Type();

    // –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é fib —Å —Ç–∏–ø–æ–º int32_t –∏ –æ–¥–Ω–∏–º –∞—Ä–≥—É–º–µ–Ω—Ç–æ–º int32_t
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", LLVMFunctionType(i32, &i32, 1, 0));

    // –°–æ–∑–¥–∞–µ–º –±–ª–æ–∫ –≤–≤–æ–¥–∞
    LLVMBasicBlockRef entry = LLVMAppendBasicBlock(fib_func, "entry");

    // –°–æ–∑–¥–∞–µ–º builder –∏ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –µ–≥–æ –≤ –±–ª–æ–∫ entry
    LLVMBuilderRef builder = LLVMCreateBuilder();
    LLVMPositionBuilderAtEnd(builder, entry);

    // –ü–æ–ª—É—á–∞–µ–º –∞—Ä–≥—É–º–µ–Ω—Ç n
    LLVMValueRef n = LLVMGetParam(fib_func, 0);

    // –ë–∞–∑–æ–≤—ã–π —Å–ª—É—á–∞–π: if n <= 1, return n
    {
        // –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º n <= 1
        LLVMValueRef cmp = LLVMBuildICmp(builder, LLVMIntSLE, n, LLVMConstInt(i32, 1, 0), "cmp");

        // –°–æ–∑–¥–∞–µ–º –±–ª–æ–∫ –¥–ª—è —É—Å–ª–æ–≤–∏—è
        LLVMBasicBlockRef then_bb = LLVMAppendBasicBlock(fib_func, "then");
        LLVMBasicBlockRef else_bb = LLVMAppendBasicBlock(fib_func, "else");

        // –£—Å–ª–æ–≤–Ω—ã–π –ø–µ—Ä–µ—Ö–æ–¥
        LLVMBuildCondBr(builder, cmp, then_bb, else_bb);

        // –û–±—Ä–∞–±–æ—Ç–∫–∞ then
        LLVMPositionBuilderAtEnd(builder, then_bb);
        LLVMBuildRet(builder, n);
        LLVMInsertIntoBasicBlock(then_bb, LLVMGetInsertBlock(builder));

        // –û–±—Ä–∞–±–æ—Ç–∫–∞ else
        LLVMPositionBuilderAtEnd(builder, else_bb);

        // –í—ã—á–∏—Å–ª—è–µ–º fib(n-1)
        LLVMValueRef n_minus_1 = LLVMBuildSub(builder, n, LLVMConstInt(i32, 1, 0), "n-1");
        LLVMValueRef call1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "call1");

        // –í—ã—á–∏—Å–ª—è–µ–º fib(n-2)
        LLVMValueRef n_minus_2 = LLVMBuildSub(builder, n, LLVMConstInt(i32, 2, 0), "n-2");
        LLVMValueRef call2 = LLVMBuildCall(builder, fib_func, &n_minus_2, 1, "call2");

        // –°–∫–ª–∞–¥—ã–≤–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã
        LLVMValueRef sum = LLVMBuildAdd(builder, call1, call2, "sum");

        // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
        LLVMBuildRet(builder, sum);
        LLVMInsertIntoBasicBlock(else_bb, LLVMGetInsertBlock(builder));
    }

    // –û—á–∏—â–∞–µ–º builder
    LLVMDisposeBuilder(builder);
}

int main() {
    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è LLVM
    LLVMInitializeNativeTarget();
    LLVMInitializeNativeAsmPrinter();

    // –°–æ–∑–¥–∞–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç –∏ –º–æ–¥—É–ª—å
    LLVMContextRef context = LLVMContextCreate();
    LLVMModuleRef module = LLVMModuleCreateWithNameInContext("fib_module", context);

    // –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é fib
    create_fib_function(module);

    // –ü–µ—á–∞—Ç–∞–µ–º IR
    char *ir = LLVMGetModuleInlineAsm(module);
