import java.util.Scanner;

public class FractionCalculator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.print("–í–≤–µ–¥–∏—Ç–µ –ø–µ—Ä–≤–æ–µ —á–∏—Å–ª–æ: ");
        double num1 = scanner.nextDouble();

        System.out.print("–í–≤–µ–¥–∏—Ç–µ –≤—Ç–æ—Ä–æ–µ —á–∏—Å–ª–æ: ");
        double num2 = scanner.nextDouble();

        System.out.println("–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é:");
        System.out.println("1 - –°–ª–æ–∂–µ–Ω–∏–µ");
        System.out.println("2 - –í—ã—á–∏—Ç–∞–Ω–∏–µ");
        System.out.println("3 - –£–º–Ω–æ–∂–µ–Ω–∏–µ");
        System.out.println("4 - –î–µ–ª–µ–Ω–∏–µ");

        int choice = scanner.nextInt();

        double result = 0;

        switch (choice) {
            case 1:
                result = num1 + num2;
                break;
            case 2:
                result = num1 - num2;
                break;
            case 3:
                result = num1 * num2;
                break;
            case 4:
                if (num2 != 0) {
                    result = num1 / num2;
                } else {
                    System.out.println("–û—à–∏–±–∫–∞: –¥–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å!");
