                return;
        }

        System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç: " + num1 + " " + getOperationSymbol(choice) + " " + num2 + " = " + result);
    }

    private static String getOperationSymbol(int choice) {
        switch (choice) {
            case 1:
                return "+";
            case 2:
                return "-";
            case 3:
                return "*";
            case 4:
                return "/";
            default:
                return "";
        }
    }
}
