#include "PluginDefinition.h"

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —É–¥–∞–ª–µ–Ω–∏—è –ª–∏—à–Ω–∏—Ö –ø—Ä–æ–±–µ–ª–æ–≤
std::wstring RemoveExtraSpaces(const std::wstring& input) {
    std::wstring result;
    bool inSpace = false;
    for (wchar_t ch : input) {
        if (ch == L' ') {
            if (!inSpace) {
                result += ch;
                inSpace = true;
            }
        } else {
            result += ch;
            inSpace = false;
        }
    }
    return result;
}

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤—Å—Ç–∞–≤–∫–∏ —Ç–µ–∫—Å—Ç–∞
void OnPaste(HWND hWnd) {
    // –ü–æ–ª—É—á–∞–µ–º –≤—Å—Ç–∞–≤–ª–µ–Ω–Ω—ã–π —Ç–µ–∫—Å—Ç
    int currentDoc = ::SendMessage(hWnd, NPPM_GETCURRENTSCINTILLA, 0, 0);
    HWND sciHandle = (HWND)::SendMessage(hWnd, NPPM_GETSCINTILLA, currentDoc, 0);

    // –ü–æ–ª—É—á–∞–µ–º –≤—ã–¥–µ–ª–µ–Ω–Ω—ã–π —Ç–µ–∫—Å—Ç (–µ—Å–ª–∏ –µ—Å—Ç—å)
    int start = ::SendMessage(sciHandle, SCI_GETSELECTIONSTART, 0, 0);
    int end = ::SendMessage(sciHandle, SCI_GETSELECTIONEND, 0, 0);
    if (start != end) {
        ::SendMessage(sciHandle, SCI_REPLACESEL, 0, (LPARAM)L"");
    }

    // –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—Å—Ç –∏–∑ –±—É—Ñ–µ—Ä–∞ –æ–±–º–µ–Ω–∞
    if (OpenClipboard(nullptr)) {
        HANDLE hData = GetClipboardData(CF_UNICODETEXT);
        if (hData) {
            wchar_t* clipboardText = (wchar_t*)GlobalLock(hData);
            if (clipboardText) {
                std::wstring cleanedText = RemoveExtraSpaces(clipboardText);
                ::SendMessage(sciHandle, SCI_REPLACESEL, 0, (LPARAM)cleanedText.c_str());
                GlobalUnlock(hData);
            }
        }
        CloseClipboard();
    }
}

// –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è –∫–æ–º–∞–Ω–¥—ã –ø–ª–∞–≥–∏–Ω–∞
extern "C" __declspec(dllexport) void beNotified(SCNotification* notifyCode) {
    if (notifyCode->nmhdr.code == NPPN_READY) {
        // –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∞ –≤—Å—Ç–∞–≤–∫–∏
        ::SendMessage(notifyCode->nmhdr.hwndFrom, NPPM_REGISTERCMDLINE, 0, (LPARAM)L"RemoveSpacesPlugin");
    }
}

// –û—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –ø–ª–∞–≥–∏–Ω–∞
extern "C" __declspec(dllexport) FuncItem funcItems[] = {
    { L"Remove Extra Spaces on Paste", OnPaste },
    { NULL, NULL }
};
