### –ê–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—ã–π –ø–æ–¥—Ö–æ–¥ (–±–µ–∑ `StringBuilder`):
–ï—Å–ª–∏ —Ç—Ä–µ–±—É–µ—Ç—Å—è —Ä–µ–∞–ª–∏–∑–æ–≤–∞—Ç—å –ø–µ—Ä–µ–≤–æ—Ä–æ—Ç —Å—Ç—Ä–æ–∫–∏ –≤—Ä—É—á–Ω—É—é, –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –º–∞—Å—Å–∏–≤ —Å–∏–º–≤–æ–ª–æ–≤:

```java
public static String reverseStringManual(String input) {
    if (input == null) {
        return null;
    }
    char[] chars = input.toCharArray();
    int left = 0;
    int right = chars.length - 1;
    while (left < right) {
        char temp = chars[left];
        chars[left] = chars[right];
        chars[right] = temp;
        left++;
        right--;
    }
    return new String(chars);
}
```

–≠—Ç–æ—Ç –º–µ—Ç–æ–¥ –º–µ–Ω—è–µ—Ç –º–µ—Å—Ç–∞–º–∏ —Å–∏–º–≤–æ–ª—ã —Å –Ω–∞—á–∞–ª–∞ –∏ –∫–æ–Ω—Ü–∞ —Å—Ç—Ä–æ–∫–∏, –ø–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ –ø—Ä–∏–±–ª–∏–∂–∞—è—Å—å –∫ —Ü–µ–Ω—Ç—Ä—É. –û–±–∞ –ø–æ–¥—Ö–æ–¥–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã, –≤—ã–±–æ—Ä –∑–∞–≤–∏—Å–∏—Ç –æ—Ç –≤–∞—à–∏—Ö –ø—Ä–µ–¥–ø–æ—á—Ç–µ–Ω–∏–π –∏ —Ç—Ä–µ–±–æ–≤–∞–Ω–∏–π –∑–∞–¥–∞—á–∏.

–í–æ—Ç —É–ø—Ä–æ—â—ë–Ω–Ω—ã–π –ø—Ä–∏–º–µ—Ä —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏ –±–∞–∑–æ–≤–æ–π –æ–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–æ–π —Å–∏—Å—Ç–µ–º—ã, –≤–¥–æ—Ö–Ω–æ–≤–ª—ë–Ω–Ω–æ–π FreeRTOS. –≠—Ç–æ—Ç –∫–æ–¥ –¥–µ–º–æ–Ω—Å—Ç—Ä–∏—Ä—É–µ—Ç –æ—Å–Ω–æ–≤–Ω—ã–µ –∫–æ–Ω—Ü–µ–ø—Ü–∏–∏, —Ç–∞–∫–∏–µ –∫–∞–∫ —Å–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á, –ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –∏ –∫–æ–Ω—Ç–µ–∫—Å—Ç–Ω—ã–π –ø–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å. –û–±—Ä–∞—Ç–∏—Ç–µ –≤–Ω–∏–º–∞–Ω–∏–µ, —á—Ç–æ —ç—Ç–æ —É—á–µ–±–Ω—ã–π –ø—Ä–∏–º–µ—Ä –∏ –Ω–µ –ø—Ä–µ–¥–Ω–∞–∑–Ω–∞—á–µ–Ω –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –≤ —Ä–µ–∞–ª—å–Ω—ã—Ö –ø—Ä–æ–µ–∫—Ç–∞—Ö –±–µ–∑ –∑–Ω–∞—á–∏—Ç–µ–ª—å–Ω–æ–π –¥–æ—Ä–∞–±–æ—Ç–∫–∏.

```c
#include <stdio.h>
#include <stdlib.h>
#include <setjmp.h>

// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–≥–æ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ –∑–∞–¥–∞—á
#define MAX_TASKS 4

// –°–æ—Å—Ç–æ—è–Ω–∏—è –∑–∞–¥–∞—á–∏
typedef enum {
    TASK_READY,
    TASK_RUNNING,
    TASK_BLOCKED
} TaskState;

// –°—Ç—Ä—É–∫—Ç—É—Ä–∞ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∑–∞–¥–∞—á–µ–π
typedef struct {
    void (*taskFunction)(void*); // –£–∫–∞–∑–∞—Ç–µ–ª—å –Ω–∞ —Ñ—É–Ω–∫—Ü–∏—é –∑–∞–¥–∞—á–∏
    void* taskParam;             // –ü–∞—Ä–∞–º–µ—Ç—Ä –∑–∞–¥–∞—á–∏
    TaskState state;             // –°–æ—Å—Ç–æ—è–Ω–∏–µ –∑–∞–¥–∞—á–∏
    int priority;                // –ü—Ä–∏–æ—Ä–∏—Ç–µ—Ç –∑–∞–¥–∞—á–∏
    jmp_buf context;             // –ö–æ–Ω—Ç–µ–∫—Å—Ç –∑–∞–¥–∞—á–∏
    char stack[1024];            // –°—Ç–µ–∫ –∑–∞–¥–∞—á–∏
} TaskControlBlock;

// –ì–ª–æ–±–∞–ª—å–Ω—ã–µ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ
static TaskControlBlock tcb[MAX_TASKS];
static int currentTask = -1;
static int taskCount = 0;

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –û–°
void OS_Init() {
    for(int i = 0; i < MAX_TASKS; i++) {
        tcb[i].state = TASK_BLOCKED;
        tcb[i].priority = 0;
    }
}

// –°–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤–æ–π –∑–∞–¥–∞—á–∏
int OS_CreateTask(void (*taskFunction)(void*), void* param, int priority) {
    if(taskCount >= MAX_TASKS) return -1; // –û—à–∏–±–∫–∞: –ø—Ä–µ–≤—ã—à–µ–Ω–æ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–¥–∞—á

    TaskControlBlock* t = &tcb[taskCount];

    t->taskFunction = taskFunction;
    t->taskParam = param;
    t->priority = priority;
    t->state = TASK_READY;

    // –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ –∑–∞–¥–∞—á–∏
    t->context->__jmpbuf[0] = (unsigned long)&t->stack[1024]; // –£—Å—Ç–∞–Ω–æ–≤–∫–∞ —É–∫–∞–∑–∞—Ç–µ–ª—è —Å—Ç–µ–∫–∞
    t->context->__jmpbuf[1] = (unsigned long)taskFunction;   // –ê–¥—Ä–µ—Å –≤–æ–∑–≤—Ä–∞—Ç–∞

    taskCount++;
    return 0;
}

// –ü–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫ –∑–∞–¥–∞—á
void OS_Scheduler() {
    int highestPriority = -1;
    int selectedTask = -1;

    // –ü–æ–∏—Å–∫ –∑–∞–¥–∞—á–∏ —Å –Ω–∞–∏–≤—ã—Å—à–∏–º –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–æ–º
    for(int i = 0; i < taskCount; i++) {
        if(tcb[i].state == TASK_READY && tcb[i].priority > highestPriority) {
            highestPriority = tcb[i].priority;
            selectedTask = i;
        }
    }

    if(selectedTask != -1) {
        if(currentTask != -1) {
            tcb[currentTask].state = TASK_READY; // –ü—Ä–µ–¥—ã–¥—É—â–∞—è –∑–∞–¥–∞—á–∞ —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è –≥–æ—Ç–æ–≤–æ–π
        }

        currentTask = selectedTask;
        tcb[currentTask].state = TASK_RUNNING;

        // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
        if(setjmp(tcb[currentTask].context) == 0) {
            longjmp(tcb[currentTask].context, 1);
        }
    }
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á–∏
void Task1(void* param) {
    while(1) {
        printf("Task1 running with param: %s\n", (char*)param);
        for(int i = 0; i < 100000; i++); // –ò–º–∏—Ç–∞—Ü–∏—è —Ä–∞–±–æ—Ç—ã
        OS_Scheduler(); // –í—Ä—É—á–Ω—É—é –≤—ã–∑—ã–≤–∞–µ–º –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫
    }
}

void Task2(void* param) {
    while(1) {
        printf("Task2 running with param: %s\n", (char*)param);
        for(int i = 0; i < 50000; i++);
        OS_Scheduler();
    }
}

int main() {
    OS_Init();

    // –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á
    OS_CreateTask(Task1, "Hello from Task1", 1);
    OS_CreateTask(Task2, "Hello from Task2", 2);

    // –ó–∞–ø—É—Å–∫ –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫–∞
    while(1) {
        OS_Scheduler();
    }

    return 0;
}
```

