### **3. –°–æ–∑–¥–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ `fib`**
```c
LLVMValueRef create_fib_function() {
    // –¢–∏–ø —Ñ—É–Ω–∫—Ü–∏–∏: i32 (–∞—Ä–≥—É–º–µ–Ω—Ç) -> i32 (–≤–æ–∑–≤—Ä–∞—Ç)
    LLVMTypeRef arg_types[] = { LLVMInt32TypeInContext(context) };
    LLVMTypeRef func_type = LLVMFunctionType(LLVMInt32TypeInContext(context), arg_types, 1, 0);

    // –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", func_type);
    LLVMSetValueName2(fib_func, "fib", strlen("fib")); // –î–ª—è –æ—Ç–ª–∞–¥–∫–∏

    // –î–æ–±–∞–≤–ª—è–µ–º –±–∞–∑–æ–≤—ã–µ –±–ª–æ–∫—ã
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlockInContext(context, fib_func, "entry");
    LLVMBasicBlockRef return0_block = LLVMAppendBasicBlockInContext(context, fib_func, "return0");
    LLVMBasicBlockRef return1_block = LLVMAppendBasicBlockInContext(context, fib_func, "return1");
    LLVMBasicBlockRef compute_block = LLVMAppendBasicBlockInContext(context, fib_func, "compute");

    // –ü–æ–ª—É—á–∞–µ–º –∞—Ä–≥—É–º–µ–Ω—Ç —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMValueRef n = LLVMGetParam(fib_func, 0);

    // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Ç–µ–∫—É—â–∏–π –±–ª–æ–∫
    LLVMPositionBuilderAtEnd(builder, entry_block);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞: n == 0?
    LLVMValueRef is_zero = LLVMBuildICmp(builder, LLVMIntEQ, n, LLVMConstInt(LLVMInt32TypeInContext(context), 0, 0), "is_zero");
    LLVMBuildCondBr(builder, is_zero, return0_block, return1_block);

    // –ë–ª–æ–∫ return0: –≤–æ–∑–≤—Ä–∞—â–∞–µ–º 0
    LLVMPositionBuilderAtEnd(builder, return0_block);
    LLVMBuildRet(builder, LLVMConstInt(LLVMInt32TypeInContext(context), 0, 0));

    // –ë–ª–æ–∫ return1: –≤–æ–∑–≤—Ä–∞—â–∞–µ–º 1
    LLVMPositionBuilderAtEnd(builder, return1_block);
    LLVMBuildRet(builder, LLVMConstInt(LLVMInt32TypeInContext(context), 1, 0));

    // –ë–ª–æ–∫ compute: —Ä–µ–∫—É—Ä—Å–∏–≤–Ω—ã–µ –≤—ã–∑–æ–≤—ã
    LLVMPositionBuilderAtEnd(builder, compute_block);
    LLVMValueRef n_minus_1 = LLVMBuildSub(builder, n, LLVMConstInt(LLVMInt32TypeInContext(context), 1, 0), "n_minus_1");
    LLVMValueRef n_minus_2 = LLVMBuildSub(builder, n, LLVMConstInt(LLVMInt32TypeInContext(context), 2, 0), "n_minus_2");

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–µ –≤—ã–∑–æ–≤—ã
    LLVMValueRef call1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "call1");
    LLVMValueRef call2 = LLVMBuildCall(builder, fib_func, &n_minus_2, 1, "call2");

    // –°–ª–æ–∂–µ–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
    LLVMValueRef result = LLVMBuildAdd(builder, call1, call2, "result");
    LLVMBuildRet(builder, result);

    return fib_func;
}
```

