```java
import java.util.Scanner;

// –ö–ª–∞—Å—Å –¥–ª—è –ø—Ä–µ–¥—Å—Ç–∞–≤–ª–µ–Ω–∏—è –¥—Ä–æ–±–∏
class Fraction {
    private int numerator;
    private int denominator;

    // –ö–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä
    public Fraction(int numerator, int denominator) {
        if (denominator == 0) {
            throw new IllegalArgumentException("–ó–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å —Ä–∞–≤–µ–Ω –Ω—É–ª—é");
        }

        this.numerator = numerator;
        this.denominator = denominator;
        simplify();
    }

    // –ú–µ—Ç–æ–¥ –¥–ª—è —Å–æ–∫—Ä–∞—â–µ–Ω–∏—è –¥—Ä–æ–±–∏
    private void simplify() {
        int gcd = gcd(Math.abs(numerator), Math.abs(denominator));

        // –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ –∑–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–π
        if (this.denominator < 0) {
            this.numerator *= -1;
            this.denominator *= -1;
        }

        this.numerator /= gcd;
        this.denominator /= gcd;
    }

    // –ê–ª–≥–æ—Ä–∏—Ç–º –ï–≤–∫–ª–∏–¥–∞ –¥–ª—è –Ω–∞—Ö–æ–∂–¥–µ–Ω–∏—è –ù–û–î
    private int gcd(int a, int b) {
        return b == 0 ? a : gcd(b, a % b);
    }

    // –°–ª–æ–∂–µ–Ω–∏–µ
    public Fraction add(Fraction other) {
        int resultNumerator = this.numerator * other.denominator + 
                             other.numerator * this.denominator;
        int resultDenominator = this.denominator * other.denominator;
        return new Fraction(resultNumerator, resultDenominator);
    }

    // –í—ã—á–∏—Ç–∞–Ω–∏–µ
    public Fraction subtract(Fraction other) {
        int resultNumerator = this.numerator * other.denominator - 
                             other.numerator * this.denominator;
        int resultDenominator = this.denominator * other.denominator;
        return new Fraction(resultNumerator, resultDenominator);
    }

    // –£–º–Ω–æ–∂–µ–Ω–∏–µ
    public Fraction multiply(Fraction other) {
        int resultNumerator = this.numerator * other.numerator;
        int resultDenominator = this.denominator * other.denominator;
        return new Fraction(resultNumerator, resultDenominator);
    }

    // –î–µ–ª–µ–Ω–∏–µ
    public Fraction divide(Fraction other) {
        if (other.numerator == 0) {
            throw new ArithmeticException("–î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ");
        }
        int resultNumerator = this.numerator * other.denominator;
        int resultDenominator = this.denominator * other.numerator;
        return new Fraction(resultNumerator, resultDenominator);
    }

    @Override
    public String toString() {
        if (denominator == 1) {
            return String.valueOf(numerator);
        }
        return numerator + "/" + denominator;
    }

    // –ì–µ—Ç—Ç–µ—Ä—ã
    public int getNumerator() {
        return numerator;
    }

    public int getDenominator() {
        return denominator;
    }
}

public class FractionCalculator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.println("–ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –¥—Ä–æ–±–µ–π");
        System.out.println("–§–æ—Ä–º–∞—Ç –≤–≤–æ–¥–∞: a/b –æ–ø–µ—Ä–∞—Ü–∏—è c/d");
        System.out.println("–ü–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º—ã–µ –æ–ø–µ—Ä–∞—Ü–∏–∏: +, -, *, /");
        System.out.print("–í–≤–µ–¥–∏—Ç–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ: ");

        try {
            String input = scanner.nextLine().trim();
            String[] parts = input.split("\\s+");

            if (parts.length != 3) {
                throw new IllegalArgumentException("–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –≤–≤–æ–¥–∞");
            }

            String fraction1Str = parts[0];
            char operation = parts[1].charAt(0);
            String fraction2Str = parts[2];

            Fraction fraction1 = parseFraction(fraction1Str);
            Fraction fraction2 = parseFraction(fraction2Str);

            Fraction result;
            switch (operation) {
                case '+':
                    result = fraction1.add(fraction2);
                    break;
                case '-':
                    result = fraction1.subtract(fraction2);
                    break;
                case '*':
                    result = fraction1.multiply(fraction2);
                    break;
                case '/':
                    result = fraction1.divide(fraction2);
                    break;
                default:
                    throw new IllegalArgumentException("–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è: " + operation);
            }

            System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç: " + result);

        } catch (Exception e) {
            System.out.println("–û—à–∏–±–∫–∞: " + e.getMessage());
        } finally {
            scanner.close();
        }
    }

    // –ú–µ—Ç–æ–¥ –¥–ª—è –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥—Ä–æ–±–∏ –∏–∑ —Å—Ç—Ä–æ–∫–∏
    private static Fraction parseFraction(String fractionStr) {
        String[] parts = fractionStr.split("/");
        if (parts.length == 1) {
