void removeSpacesFromSelection() {
    int nbSel = (int)SendMessage(nppData._nppHandle, NPPM_GETNBOPENFILES, 0, 0);
    for (int i = 0; i < nbSel; ++i) {
        TCHAR text[1024];
        int len = (int)SendMessage(nppData._nppHandle, NPPM_GETTEXT, sizeof(text), (LPARAM)text);
        if (len > 0) {
            char* cleanText = new char[len + 1];
            strcpy_s(cleanText, len + 1, text);
            for (int j = 0; j < len; ++j) {
                if (cleanText[j] == ' ') {
                    memmove(&cleanText[j], &cleanText[j + 1], len - j);
                    len--;
                    j--;
                }
            }
            SendMessage(nppData._nppHandle, NPPM_SETTEXT, 0, (LPARAM)cleanText);
            delete[] cleanText;
        }
    }
}

