## ‚úÖ –®–∞–≥ 3: –†–µ–∞–ª–∏–∑–∞—Ü–∏—è –Ω–∞ C

```c
#include <llvm-c/Core.h>
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/Transforms/Scalar.h>
#include <stdio.h>

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è LLVM
void init_llvm() {
    LLVMInitializeNativeTarget();
    LLVMInitializeNativeAsmPrinter();
    LLVMInitializeNativeAsmParser();
}

// –û—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è
int main() {
    // –®–∞–≥ 1: –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
    init_llvm();

    LLVMContextRef context = LLVMContextCreate();
    LLVMModuleRef module = LLVMModuleCreateWithNameInContext("fib_module", context);
    LLVMBuilderRef builder = LLVMCreateBuilderInContext(context);

    // –®–∞–≥ 2: –°–æ–∑–¥–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ fib(n)
    LLVMTypeRef int_type = LLVMInt32TypeInContext(context);
    LLVMTypeRef arg_types[] = { int_type };
    LLVMTypeRef func_type = LLVMFunctionType(int_type, arg_types, 1, 0);
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", func_type);

    // –®–∞–≥ 3: –°–æ–∑–¥–∞–Ω–∏–µ –±–ª–æ–∫–æ–≤
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlockInContext(context, fib_func, "entry");
    LLVMBasicBlockRef base_case = LLVMAppendBasicBlockInContext(context, fib_func, "base_case");
    LLVMBasicBlockRef recurse = LLVMAppendBasicBlockInContext(context, fib_func, "recurse");

    // –®–∞–≥ 4: –í—Ö–æ–¥ –≤ –±–ª–æ–∫ entry
    LLVMPositionBuilderAtEnd(builder, entry_block);

    LLVMValueRef n = LLVMGetParam(fib_func, 0);
    LLVMValueRef zero = LLVMConstInt(int_type, 0, 0);
    LLVMValueRef one = LLVMConstInt(int_type, 1, 0);
    LLVMValueRef two = LLVMConstInt(int_type, 2, 0);

    LLVMValueRef cond = LLVMBuildICmp(builder, LLVMIntEQ, n, zero, "is_zero");
    LLVMBuildCondBr(builder, cond, base_case, recurse);

    // –ë–ª–æ–∫ base_case
    LLVMPositionBuilderAtEnd(builder, base_case);
    LLVMBuildRet(builder, zero);

    // –ë–ª–æ–∫ recurse
    LLVMPositionBuilderAtEnd(builder, recurse);

    LLVMValueRef n_minus_one = LLVMBuildSub(builder, n, one, "n-1");
    LLVMValueRef n_minus_two = LLVMBuildSub(builder, n, two, "n-2");

    LLVMValueRef call1 = LLVMBuildCall2(builder, func_type, fib_func, &n_minus_one, 1, "call1");
    LLVMValueRef call2 = LLVMBuildCall2(builder, func_type, fib_func, &n_minus_two, 1, "call2");

    LLVMValueRef result = LLVMBuildAdd(builder, call1, call2, "result");
    LLVMBuildRet(builder, result);

    // –®–∞–≥ 5: –ü—Ä–æ–≤–µ—Ä–∫–∞ –º–æ–¥—É–ª—è
    char *error;
    if (LLVMVerifyModule(module, LLVMPrintMessageAction, &error)) {
        fprintf(stderr, "–û—à–∏–±–∫–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ –º–æ–¥—É–ª—è: %s\n", error);
        LLVMDisposeMessage(error);
        return 1;
    }

    // –®–∞–≥ 6: –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ IR –≤ —Ñ–∞–π–ª
    FILE *file = fopen("fib.ll", "w");
    if (!file) {
        perror("–ù–µ —É–¥–∞–ª–æ—Å—å –æ—Ç–∫—Ä—ã—Ç—å —Ñ–∞–π–ª –¥–ª—è –∑–∞–ø–∏—Å–∏");
        return 1;
    }

    LLVM_DUMP_METHOD(LLVMWriteBitcodeToFile)(module, file);
    fclose(file);

    // –®–∞–≥ 7: –û—Å–≤–æ–±–æ–∂–¥–µ–Ω–∏–µ —Ä–µ—Å—É—Ä—Å–æ–≤
    LLVMDisposeBuilder(builder);
    LLVMDisposeModule(module);
    LLVMContextDispose(context);

    printf("IR-–∫–æ–¥ —É—Å–ø–µ—à–Ω–æ –∑–∞–ø–∏—Å–∞–Ω –≤ fib.ll\n");
    return 0;
}
```

