### –ü—Ä–∏–º–µ—Ä –∫–æ–¥–∞ –Ω–∞ C –¥–ª—è FFT –≤ Vitis HLS

```cpp
#include <ap_int.h>
#include <hls_math.h>
#include <ap_axi_stream.h>

// –ü–∞—Ä–∞–º–µ—Ç—Ä—ã FFT
#define N 1024 // –î–ª–∏–Ω–∞ FFT (–¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å —Å—Ç–µ–ø–µ–Ω—å—é –¥–≤–æ–π–∫–∏)
#define DATA_TYPE ap_float // –¢–∏–ø –¥–∞–Ω–Ω—ã—Ö (float, double, ap_float –∏ —Ç.–¥.)

// –°—Ç—Ä—É–∫—Ç—É—Ä–∞ –¥–ª—è –ø–µ—Ä–µ–¥–∞—á–∏ –¥–∞–Ω–Ω—ã—Ö
struct stream_data {
    ap_uint<32> valid;
    ap_float data[N];
};

// –ü–æ—Ç–æ–∫ –¥–∞–Ω–Ω—ã—Ö
typedef ap_axi_stream<stream_data> stream_type;

// –§—É–Ω–∫—Ü–∏—è FFT
void fft(ap_float* in, ap_float* out, int n) {
    // –†–µ–∞–ª–∏–∑–∞—Ü–∏—è FFT –Ω–∞ –æ—Å–Ω–æ–≤–µ –±–∏–Ω–∞—Ä–Ω–æ–≥–æ –¥–µ—Ä–µ–≤–∞
    // –≠—Ç–æ —É–ø—Ä–æ—â–µ–Ω–Ω–∞—è –≤–µ—Ä—Å–∏—è; –¥–ª—è —Ä–µ–∞–ª—å–Ω–æ–≥–æ –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è —Ç—Ä–µ–±—É–µ—Ç—Å—è –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è
    int i, j, k, m, l, n1, n2;
    ap_float w, temp;
    ap_float *x = in;
    ap_float *y = out;

    n1 = n / 2;
    for (i = 0; i < n; i++) {
        if (i < n1) {
            j = i;
            for (k = 0; k < log2(n); k++) {
                l = j ^ (1 << k);
                if (l > j) {
                    swap(x[j], x[l]);
                }
                j = l;
            }
        }
    }

    for (m = 1; m < n; m *= 2) {
        n2 = n / m;
        for (i = 0; i < n; i += m) {
            for (k = 0; k < n2; k++) {
                j = i + k;
                l = j + n2;
                w = cos(2 * M_PI * k / m);
                temp = x[l] * w;
                x[l] = x[j] - temp;
                x[j] = x[j] + temp;
            }
        }
    }

    for (i = 0; i < n; i++) {
        out[i] = x[i];
    }
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –ø–æ—Ç–æ–∫–∞ –¥–∞–Ω–Ω—ã—Ö
void fft_stream(stream_type& in, stream_type& out) {
    ap_float in_data[N];
    ap_float out_data[N];

    while (true) {
        // –ü–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –∏–∑ –ø–æ—Ç–æ–∫–∞
        stream_data data;
        data.valid = in.read().valid;
        if (data.valid) {
            for (int i = 0; i < N; i++) {
                in_data[i] = data.data[i];
            }

            // –í—ã–∑–æ–≤ FFT
            fft(in_data, out_data, N);

            // –û—Ç–ø—Ä–∞–≤–∫–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ –≤ –ø–æ—Ç–æ–∫
            stream_data result;
            for (int i = 0; i < N; i++) {
                result.data[i] = out_data[i];
            }
            out.write(result);
        }
    }
}
```

