            self.balance += amount
            self.history.append(f"Deposited {amount} (Balance: {self.balance})")
            return True
        return False

    def withdraw(self, amount):
        if 0 < amount <= self.balance:
            self.balance -= amount
            self.history.append(f"Withdrew {amount} (Balance: {self.balance})")
            return True
        return False

    def get_history(self):
