```cpp
#include "fft.h"
#include <cmath>
#include <cstdlib>

namespace fft {

void fft(std::complex<float>* in, std::complex<float>* out, int n) {
    // –ü—Ä–æ–≤–µ—Ä–∫–∞, —á—Ç–æ n —è–≤–ª—è–µ—Ç—Å—è —Å—Ç–µ–ø–µ–Ω—å—é –¥–≤–æ–π–∫–∏
    if ((n & (n - 1)) != 0) {
        throw std::domain_error("n must be a power of two");
    }

    // –ï—Å–ª–∏ n = 1, —Ç–æ –≤—ã—Ö–æ–¥–Ω–æ–π –º–∞—Å—Å–∏–≤ —Ä–∞–≤–µ–Ω –≤—Ö–æ–¥–Ω–æ–º—É
    if (n == 1) {
        for (int i = 0; i < n; ++i) {
            out[i] = in[i];
        }
        return;
    }

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è FFT
    int m = n / 2;
    fft(in, out, m);
    fft(in + m, out + m, m);

    for (int i = 0; i < m; ++i) {
        std::complex<float> temp = out[i] + out[i + m] * std::polar(1.0f, -2.0f * M_PI * i / n);
        out[i] = out[i] + temp;
        out[i + m] = out[i] - temp;
    }
}

} // namespace fft
```

