## ‚úÖ –ü—Ä–∏–º–µ—Ä –∫–æ–¥–∞ –Ω–∞ C —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º LLVM-C API

```c
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/TargetMachine.h>
#include <llvm-c/IR.h>
#include <llvm-c/Utils.h>
#include <stdio.h>

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ fib
void generate_fib_function() {
    // 1. –°–æ–∑–¥–∞–Ω–∏–µ –º–æ–¥—É–ª—è
    LLVMModuleRef module = LLVMModuleCreateWithName("fib_module");
    LLVMTypeRef i32_type = LLVMInt32Type();
    LLVMTypeRef i64_type = LLVMInt64Type();

    // 2. –°–æ–∑–¥–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ fib(i32) -> i32
    LLVMFunctionRef fib_func = LLVMFunctionCreateNamed(module, "fib", LLVMFunctionType(LLVMInt32Type(), &i32_type, 1, false));

    // 3. –°–æ–∑–¥–∞–Ω–∏–µ –±–∞–π—Ç–∫–æ–¥–∞ –¥–ª—è —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlock(fib_func, "entry");
    LLVMBuilderRef builder = LLVMCreateBuilder();

    // 4. –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –∏–Ω—Å—Ç—Ä—É–∫—Ü–∏–π
    LLVMValueRef param = LLVMGetParam(fib_func, 0);
    LLVMValueRef zero = LLVMConstInt(i32_type, 0, false);
    LLVMValueRef one = LLVMConstInt(i32_type, 1, false);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –Ω–æ–ª—å –∏–ª–∏ –µ–¥–∏–Ω–∏—Ü—É
    LLVMValueRef is_zero = LLVMICmpSLT(builder, param, zero);
    LLVMValueRef is_one = LLVMICmpEQ(builder, param, one);

    // –£—Å–ª–æ–≤–Ω–∞—è –≤–µ—Ç–≤–ª–µ–Ω–∏–µ
    LLVMValueRef then_block = LLVMAppendBasicBlock(fib_func, "then");
    LLVMValueRef else_block = LLVMAppendBasicBlock(fib_func, "else");

    LLVMBranchConditional(builder, is_zero, then_block, else_block);

    // –¢–µ–ª–æ then_block: return 0
    LLVMPositionAtEnd(builder, then_block);
    LLVMReturn(builder, zero);

    // –¢–µ–ª–æ else_block: return 1
    LLVMPositionAtEnd(builder, else_block);
    LLVMReturn(builder, one);

    // –í–µ—Ç–≤–ª–µ–Ω–∏–µ –¥–ª—è n > 1
    LLVMValueRef next_block = LLVMAppendBasicBlock(fib_func, "next");
    LLVMValueRef prev_block = LLVMAppendBasicBlock(fib_func, "prev");

    LLVMValueRef sub = LLVMSub(builder, param, one);
    LLVMValueRef call_next = LLVMCallFunction(builder, fib_func, &sub, 1, false);
    LLVMValueRef call_prev = LLVMCallFunction(builder, fib_func, param, 1, false);

    LLVMBranchConditional(builder, call_next, next_block, prev_block);

    // –¢–µ–ª–æ next_block: return call_prev + call_next
    LLVMPositionAtEnd(builder, next_block);
    LLVMValueRef add = LLVMAdd(builder, call_prev, call_next);
    LLVMReturn(builder, add);

