4. `System.out.println(...)` –≤—ã–≤–æ–¥–∏—Ç –ø–µ—Ä–µ–≤–µ—Ä–Ω—É—Ç—É—é —Å—Ç—Ä–æ–∫—É.

### –†–µ–∑—É–ª—å—Ç–∞—Ç:
```
–ü–µ—Ä–µ–≤–µ—Ä–Ω—É—Ç–∞—è —Å—Ç—Ä–æ–∫–∞: —ã—Ä–∫–æ—Ç—Å —Ä–∏–º–µ–ü
```

–ï—Å–ª–∏ –≤—ã —Ö–æ—Ç–∏—Ç–µ —Ä–µ–∞–ª–∏–∑–æ–≤–∞—Ç—å —ç—Ç–æ –±–µ–∑ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è `StringBuilder`, –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Ü–∏–∫–ª:

```java
public class ReverseString {
    public static void main(String[] args) {
        String original = "–ü—Ä–∏–º–µ—Ä —Å—Ç—Ä–æ–∫–∏";
        StringBuilder reversed = new StringBuilder();

        for (int i = original.length() - 1; i >= 0; i--) {
            reversed.append(original.charAt(i));
        }

        System.out.println("–ü–µ—Ä–µ–≤–µ—Ä–Ω—É—Ç–∞—è —Å—Ç—Ä–æ–∫–∞: " + reversed.toString());
    }
}
```

–û–±–∞ –≤–∞—Ä–∏–∞–Ω—Ç–∞ —Ä–∞–±–æ—Ç–∞—é—Ç –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ –∏ –∏–º–µ—é—Ç —Å–ª–æ–∂–Ω–æ—Å—Ç—å **O(n)**.

–í–æ—Ç –ø—Ä–∏–º–µ—Ä —É–ø—Ä–æ—â–µ–Ω–Ω–æ–π –æ–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–æ–π —Å–∏—Å—Ç–µ–º—ã –≤ —Ä–µ–∞–ª—å–Ω–æ–º –≤—Ä–µ–º–µ–Ω–∏ (RTOS), –ø–æ—Ö–æ–∂–µ–π –Ω–∞ FreeRTOS, —Å –±–∞–∑–æ–≤—ã–º–∏ —Ñ—É–Ω–∫—Ü–∏—è–º–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∑–∞–¥–∞—á–∞–º–∏ –∏ –æ—á–µ—Ä–µ–¥—è–º–∏. –≠—Ç–æ—Ç –∫–æ–¥ –¥–µ–º–æ–Ω—Å—Ç—Ä–∏—Ä—É–µ—Ç –∫–ª—é—á–µ–≤—ã–µ –∫–æ–Ω—Ü–µ–ø—Ü–∏–∏, –Ω–æ –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –ø–æ–ª–Ω–æ—Ü–µ–Ω–Ω–æ–π —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–µ–π FreeRTOS.

```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å—Ç—Ä—É–∫—Ç—É—Ä—ã –∑–∞–¥–∞—á–∏
typedef struct {
    void (*task_func)(void*);      // –§—É–Ω–∫—Ü–∏—è –∑–∞–¥–∞—á–∏
    void* arg;                     // –ê—Ä–≥—É–º–µ–Ω—Ç—ã
    int priority;                  // –ü—Ä–∏–æ—Ä–∏—Ç–µ—Ç (0 - –≤—ã—Å—à–∏–π)
    int state;                     // –°–æ—Å—Ç–æ—è–Ω–∏–µ –∑–∞–¥–∞—á–∏ (0 - –≥–æ—Ç–æ–≤–∞, 1 - –≤—ã–ø–æ–ª–Ω–µ–Ω–∞)
} Task_t;

// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å—Ç—Ä—É–∫—Ç—É—Ä—ã –æ—á–µ—Ä–µ–¥–∏
typedef struct {
    char* buffer;
    int head;
    int tail;
    int size;
    int capacity;
} Queue_t;

// –ì–ª–æ–±–∞–ª—å–Ω—ã–µ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ
Task_t* tasks[10];  // –ú–∞—Å—Å–∏–≤ –∑–∞–¥–∞—á
int task_count = 0;
Queue_t queue;

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –æ—á–µ—Ä–µ–¥–∏
void init_queue(int capacity) {
    queue.capacity = capacity;
    queue.size = 0;
    queue.head = 0;
    queue.tail = 0;
    queue.buffer = (char*)malloc(capacity * sizeof(char));
}

// –û—Ç–ø—Ä–∞–≤–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏—è –≤ –æ—á–µ—Ä–µ–¥—å
void send_to_queue(char* message) {
    if (queue.size < queue.capacity) {
        strcpy(&queue.buffer[queue.tail], message);
        queue.tail = (queue.tail + strlen(message) + 1) % queue.capacity;
        queue.size++;
    }
}

// –ü–æ–ª—É—á–µ–Ω–∏–µ —Å–æ–æ–±—â–µ–Ω–∏—è –∏–∑ –æ—á–µ—Ä–µ–¥–∏
char* receive_from_queue() {
    if (queue.size > 0) {
        char* msg = (char*)malloc((queue.capacity) * sizeof(char));
        strcpy(msg, &queue.buffer[queue.head]);
        queue.head = (queue.head + strlen(msg) + 1) % queue.capacity;
        queue.size--;
        return msg;
    }
    return NULL;
}

// –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á–∏
void create_task(void (*func)(void*), void* arg, int priority) {
    if (task_count < 10) {
        tasks[task_count] = (Task_t*)malloc(sizeof(Task_t));
        tasks[task_count]->task_func = func;
        tasks[task_count]->arg = arg;
        tasks[task_count]->priority = priority;
        tasks[task_count]->state = 0; // –ì–æ—Ç–æ–≤–∞ –∫ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—é
        task_count++;
    }
}

// –ü–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫ –∑–∞–¥–∞—á
void scheduler() {
    int highest_priority = -1;
    int selected_task = -1;

    for (int i = 0; i < task_count; i++) {
        if (tasks[i]->state == 0 && tasks[i]->priority > highest_priority) {
            highest_priority = tasks[i]->priority;
            selected_task = i;
        }
    }

    if (selected_task != -1) {
        printf("–ó–∞–ø—É—Å–∫ –∑–∞–¥–∞—á–∏ —Å –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–æ–º %d\n", highest_priority);
        tasks[selected_task]->task_func(tasks[selected_task]->arg);
        tasks[selected_task]->state = 1; // –ó–∞–≤–µ—Ä—à–µ–Ω–∞
    }
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á
void task1(void* arg) {
    while (1) {
        printf("–ó–∞–¥–∞—á–∞ 1: –û—Ç–ø—Ä–∞–≤–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏—è –≤ –æ—á–µ—Ä–µ–¥—å\n");
        send_to_queue("–°–æ–æ–±—â–µ–Ω–∏–µ –æ—Ç –∑–∞–¥–∞—á–∏ 1");
        sleep(1);
    }
}

void task2(void* arg) {
    while (1) {
        char* msg = receive_from_queue();
        if (msg != NULL) {
            printf("–ó–∞–¥–∞—á–∞ 2: –ü–æ–ª—É—á–µ–Ω–æ —Å–æ–æ–±—â–µ–Ω–∏–µ - %s\n", msg);
            free(msg);
        }
        sleep(1);
    }
}

int main() {
    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –æ—á–µ—Ä–µ–¥–∏
    init_queue(100);

    // –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á
    create_task(task1, NULL, 1);
    create_task(task2, NULL, 2);

    // –û—Å–Ω–æ–≤–Ω–æ–π —Ü–∏–∫–ª –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫–∞
    while (1) {
        scheduler();
    }

    return 0;
}
```

### –û—Å–Ω–æ–≤–Ω—ã–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã:
1. **–ó–∞–¥–∞—á–∏**:
   - –ö–∞–∂–¥–∞—è –∑–∞–¥–∞—á–∞ –∏–º–µ–µ—Ç —Ñ—É–Ω–∫—Ü–∏—é, –∞—Ä–≥—É–º–µ–Ω—Ç—ã, –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç –∏ —Å–æ—Å—Ç–æ—è–Ω–∏–µ.
   - –ó–∞–¥–∞—á–∏ —Å–æ–∑–¥–∞—é—Ç—Å—è —Å –ø–æ–º–æ—â—å—é `create_task()`.

2. **–û—á–µ—Ä–µ–¥–∏**:
   - –†–µ–∞–ª–∏–∑–æ–≤–∞–Ω–∞ –ø—Ä–æ—Å—Ç–∞—è –æ—á–µ—Ä–µ–¥—å —Å–æ–æ–±—â–µ–Ω–∏–π —Å –æ—Ç–ø—Ä–∞–≤–∫–æ–π –∏ –ø–æ–ª—É—á–µ–Ω–∏–µ–º.
   - –ò—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è –¥–ª—è —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–∏ –º–µ–∂–¥—É –∑–∞–¥–∞—á–∞–º–∏.

3. **–ü–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫**:
   - –í—ã–±–∏—Ä–∞–µ—Ç –∑–∞–¥–∞—á—É —Å —Å–∞–º—ã–º –≤—ã—Å–æ–∫–∏–º –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–æ–º –∏–∑ —Å–ø–∏—Å–∫–∞ –≥–æ—Ç–æ–≤—ã—Ö.
   - –í—ã–ø–æ–ª–Ω—è–µ—Ç –∑–∞–¥–∞—á—É, –∑–∞—Ç–µ–º –ø–æ–º–µ—á–∞–µ—Ç –µ—ë –∫–∞–∫ –∑–∞–≤–µ—Ä—à–µ–Ω–Ω—É—é.

4. **–ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è**:
   - –î–≤–µ –∑–∞–¥–∞—á–∏ –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤—É—é—Ç —á–µ—Ä–µ–∑ –æ—á–µ—Ä–µ–¥—å: –æ–¥–Ω–∞ –æ—Ç–ø—Ä–∞–≤–ª—è–µ—Ç —Å–æ–æ–±—â–µ–Ω–∏—è, –¥—Ä—É–≥–∞—è –ø–æ–ª—É—á–∞–µ—Ç.

### –û–≥—Ä–∞–Ω–∏—á–µ–Ω–∏—è:
- –≠—Ç–æ —É–ø—Ä–æ—â–µ–Ω–Ω–∞—è –≤–µ—Ä—Å–∏—è, –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞—é—â–∞—è –∫–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ, –ø—Ä–µ—Ä—ã–≤–∞–Ω–∏—è –∏ –¥—Ä—É–≥–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ FreeRTOS.
- –ù–µ –≤–∫–ª—é—á–∞–µ—Ç —É–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –ø–∞–º—è—Ç—å—é, –¥–∏–Ω–∞–º–∏—á–µ—Å–∫–æ–µ –≤—ã–¥–µ–ª–µ–Ω–∏–µ –∏ —Ç.–¥.
- –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑—É–µ—Ç `sleep()` –¥–ª—è –∏–º–∏—Ç–∞—Ü–∏–∏ –∑–∞–¥–µ—Ä–∂–∫–∏, —á—Ç–æ –Ω–µ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É–µ—Ç —Ä–µ–∞–ª—å–Ω–æ–º—É RTOS.

### –ö–∞–∫ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å:
1. –°–∫–æ–º–ø–∏–ª–∏—Ä—É–π—Ç–µ –∫–æ–¥ —Å –ø–æ–º–æ—â—å—é –∫–æ–º–ø–∏–ª—è—Ç–æ—Ä–∞ C (–Ω–∞–ø—Ä–∏–º–µ—Ä, `gcc`).
2. –ó–∞–ø—É—Å—Ç–∏—Ç–µ –ø—Ä–æ–≥—Ä–∞–º–º—É, —á—Ç–æ–±—ã —É–≤–∏–¥–µ—Ç—å –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏–µ –∑–∞–¥–∞—á —á–µ—Ä–µ–∑ –æ—á–µ—Ä–µ–¥—å.

–≠—Ç–æ—Ç –ø—Ä–∏–º–µ—Ä –¥–µ–º–æ–Ω—Å—Ç—Ä–∏—Ä—É–µ—Ç –±–∞–∑–æ–≤—ã–µ –ø—Ä–∏–Ω—Ü–∏–ø—ã RTOS, –Ω–æ –¥–ª—è –ø–æ–ª–Ω–æ—Ü–µ–Ω–Ω–æ–π —Å–∏—Å—Ç–µ–º—ã –ø–æ—Ç—Ä–µ–±—É–µ—Ç—Å—è –≥–æ—Ä–∞–∑–¥–æ –±–æ–ª–µ–µ —Å–ª–æ–∂–Ω–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è.

–í–æ—Ç –ø—Ä–æ—Å—Ç–æ–π –ø—Ä–∏–º–µ—Ä –ø—Ä–æ–≥—Ä–∞–º–º—ã, –∫–æ—Ç–æ—Ä–∞—è –∏—Å–ø–æ–ª—å–∑—É–µ—Ç `<canvas>` –∏ –º–µ—Ç–æ–¥ `getImageData` –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è –ø–∏–∫—Å–µ–ª—å–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö:

```html
<!DOCTYPE html>
<html>
<head>
  <title>Canvas getImageData Example</title>
  <style>
    canvas {
      border: 1px solid #000;
    }
  </style>
</head>
<body>
  <h2>–ü—Ä–∏–º–µ—Ä —Ä–∞–±–æ—Ç—ã —Å canvas –∏ getImageData</h2>
  <canvas id="myCanvas" width="400" height="200"></canvas>
  <script>
    // –ü–æ–ª—É—á–∞–µ–º —ç–ª–µ–º–µ–Ω—Ç canvas
    const canvas = document.getElementById('myCanvas');
    const ctx = canvas.getContext('2d');

    // –†–∏—Å—É–µ–º –ø—Ä–æ—Å—Ç–æ–π –ø—Ä—è–º–æ—É–≥–æ–ª—å–Ω–∏–∫ –Ω–∞ canvas
    ctx.fillStyle = 'lightblue';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // –ü–æ–ª—É—á–∞–µ–º –ø–∏–∫—Å–µ–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    const data = imageData.data;

    // –ò–∑–º–µ–Ω—è–µ–º —Ü–≤–µ—Ç –≤—Å–µ—Ö –ø–∏–∫—Å–µ–ª–µ–π (–Ω–∞–ø—Ä–∏–º–µ—Ä, –¥–µ–ª–∞–µ–º –∏—Ö —Å–µ—Ä—ã–º–∏)
    for (let i = 0; i < data.length; i += 4) {
      const avg = (data[i] + data[i+1] + data[i+2]) / 3;
      data[i] = avg;       // –ö—Ä–∞—Å–Ω—ã–π
      data[i+1] = avg;     // –ó–µ–ª–µ–Ω—ã–π
      data[i+2] = avg;     // –°–∏–Ω–∏–π
