    // –û–±–Ω–æ–≤–ª—è–µ–º canvas —Å –∏–∑–º–µ–Ω–µ–Ω–Ω—ã–º–∏ –¥–∞–Ω–Ω—ã–º–∏
    ctx.putImageData(imageData, 0, 0);
  </script>
</body>
</html>
```

### –ß—Ç–æ –¥–µ–ª–∞–µ—Ç —ç—Ç–æ—Ç –∫–æ–¥:
1. –°–æ–∑–¥–∞–µ—Ç canvas —Ä–∞–∑–º–µ—Ä–æ–º 400x200 –ø–∏–∫—Å–µ–ª–µ–π
2. –†–∏—Å—É–µ—Ç —Å–≤–µ—Ç–ª–æ-–≥–æ–ª—É–±–æ–π –ø—Ä—è–º–æ—É–≥–æ–ª—å–Ω–∏–∫
3. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç `getImageData()` –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –º–∞—Å—Å–∏–≤–∞ –ø–∏–∫—Å–µ–ª—å–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö
4. –ú–æ–¥–∏—Ñ–∏—Ü–∏—Ä—É–µ—Ç –¥–∞–Ω–Ω—ã–µ: –ø—Ä–µ–æ–±—Ä–∞–∑—É–µ—Ç –∫–∞–∂–¥—ã–π –ø–∏–∫—Å–µ–ª—å –≤ —Å–µ—Ä—ã–π —Ü–≤–µ—Ç
5. –û–±–Ω–æ–≤–ª—è–µ—Ç canvas —Å –ø–æ–º–æ—â—å—é `putImageData()`

### –ö–∞–∫ —ç—Ç–æ —Ä–∞–±–æ—Ç–∞–µ—Ç:
- `getImageData(x, y, width, height)` –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç –æ–±—ä–µ–∫—Ç —Å –º–∞—Å—Å–∏–≤–æ–º –¥–∞–Ω–Ω—ã—Ö –ø–∏–∫—Å–µ–ª–µ–π
- –î–∞–Ω–Ω—ã–µ –ø—Ä–µ–¥—Å—Ç–∞–≤–ª–µ–Ω—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ RGBA (–∫—Ä–∞—Å–Ω—ã–π, –∑–µ–ª–µ–Ω—ã–π, —Å–∏–Ω–∏–π, –∞–ª—å—Ñ–∞-–∫–∞–Ω–∞–ª)
- –ú–æ–∂–Ω–æ –∏–∑–º–µ–Ω–∏—Ç—å –∑–Ω–∞—á–µ–Ω–∏—è –≤ –º–∞—Å—Å–∏–≤–µ –¥–ª—è –º–∞–Ω–∏–ø—É–ª—è—Ü–∏–∏ —Å —Ü–≤–µ—Ç–∞–º–∏
- `putImageData()` –ø—Ä–∏–º–µ–Ω—è–µ—Ç –∏–∑–º–µ–Ω–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –æ–±—Ä–∞—Ç–Ω–æ –Ω–∞ canvas

–≠—Ç–æ—Ç –ø—Ä–∏–º–µ—Ä –¥–µ–º–æ–Ω—Å—Ç—Ä–∏—Ä—É–µ—Ç –±–∞–∑–æ–≤–æ–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ `getImageData` –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –ø–∏–∫—Å–µ–ª—å–Ω—ã–º–∏ –¥–∞–Ω–Ω—ã–º–∏. –í—ã –º–æ–∂–µ—Ç–µ —Ä–∞—Å—à–∏—Ä–∏—Ç—å –µ–≥–æ, –¥–æ–±–∞–≤–∏–≤ —Ñ–∏–ª—å—Ç—Ä—ã, –æ–±—Ä–∞–±–æ—Ç–∫—É –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –∏–ª–∏ –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏–µ —Å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–º.

–í–æ—Ç –ø—Ä–∏–º–µ—Ä –∫–æ–¥–∞ –Ω–∞ C –¥–ª—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏ –±—ã—Å—Ç—Ä–æ–≥–æ –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏—è –§—É—Ä—å–µ (FFT) —Å –≤—ã—Å–æ–∫–æ–π —á–∞—Å—Ç–æ—Ç–æ–π –¥–∏—Å–∫—Ä–µ—Ç–∏–∑–∞—Ü–∏–∏ –≤ Vitis HLS. –≠—Ç–æ—Ç –∫–æ–¥ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç –∏—Ç–µ—Ä–∞—Ç–∏–≤–Ω—ã–π –∞–ª–≥–æ—Ä–∏—Ç–º Cooley-Tukey –∏ –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω –¥–ª—è –∞–ø–ø–∞—Ä–∞—Ç–Ω–æ–π —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏ –Ω–∞ FPGA —Å –ø–æ–º–æ—â—å—é –¥–∏—Ä–µ–∫—Ç–∏–≤ Vitis HLS.

```c
#include <ap_fixed.h>
#include <hls_stream.h>
#include <complex>

// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–∞ –¥–∞–Ω–Ω—ã—Ö –¥–ª—è –∫–æ–º–ø–ª–µ–∫—Å–Ω—ã—Ö —á–∏—Å–µ–ª
typedef ap_fixed<16, 1> data_type;
typedef std::complex<data_type> complex_data;

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è FFT
void fft(hls::stream<complex_data>& input_stream, hls::stream<complex_data>& output_stream, int N) {
    // –ü—Ä–æ–≤–µ—Ä–∫–∞, —è–≤–ª—è–µ—Ç—Å—è –ª–∏ N —Å—Ç–µ–ø–µ–Ω—å—é –¥–≤–æ–π–∫–∏
    if ((N & (N - 1)) != 0) {
        return; // –û—à–∏–±–∫–∞: N –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —Å—Ç–µ–ø–µ–Ω—å—é –¥–≤–æ–π–∫–∏
    }

    // –ë–∏—Ç-—Ä–µ–≤–µ—Ä—Å–∏–≤–Ω—ã–π –ø–æ—Ä—è–¥–æ–∫
    int logN = log2(N);
    for (int i = 0; i < N; i++) {
        int j = 0;
        for (int k = 1; k < logN; k++) {
            j = (j << 1) | ((i >> (logN - 1 - k)) & 1);
        }
        if (i > j) {
            complex_data temp = input_stream.read();
            input_stream.write(temp);
        }
    }

    // –ò—Ç–µ—Ä–∞—Ç–∏–≤–Ω–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è FFT
    for (int s = 1; s < logN; s++) {
        int m = 1 << s;
        int n = 1 << (s - 1);
        complex_data w = complex_data(1.0, 0.0);
        complex_data wn = complex_data(cos(2 * M_PI / m), sin(2 * M_PI / m));
        for (int k = 0; k < n; k++) {
            complex_data t = w;
            for (int j = 0; j < n; j++) {
                complex_data x = input_stream.read();
                complex_data y = input_stream.read();
                complex_data z = x + y * t;
                complex_data u = x - y * t;
                output_stream.write(z);
                output_stream.write(u);
                t = t * wn;
            }
            w = w * wn;
        }
    }

    // –ü–µ—Ä–µ–º–µ—à–∏–≤–∞–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö
    for (int i = 0; i < N; i++) {
        output_stream.write(input_stream.read());
    }
}
