#include <llvm-c/llvm.h>
#include <stdio.h>

int main() {
    // 1. –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
    LLVMContextRef context = LLVMContextCreate();
    if (!context) {
        fprintf(stderr, "Failed to create LLVM context.\n");
        return 1;
    }

    // 2. –°–æ–∑–¥–∞–Ω–∏–µ –º–æ–¥—É–ª—è
    LLVMModuleRef module = LLVMModuleCreateWithName("fib_module");
    if (!module) {
        fprintf(stderr, "Failed to create LLVM module.\n");
        LLVMContextDispose(context);
        return 1;
    }

    // 3. –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–∞ —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMTypeRef i32_type = LLVMInt32Type();
    LLVMTypeRef fib_func_type = LLVMFunctionType(i32_type, &i32_type, 1, 0);

    // 4. –î–æ–±–∞–≤–ª–µ–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ fib –≤ –º–æ–¥—É–ª—å
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", fib_func_type);
    if (!fib_func) {
        fprintf(stderr, "Failed to add function 'fib' to module.\n");
        LLVMDisposeModule(module);
        LLVMContextDispose(context);
        return 1;
    }

    // 5. –°–æ–∑–¥–∞–Ω–∏–µ builder –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –∏–Ω—Å—Ç—Ä—É–∫—Ü–∏–π
    LLVMBuilderRef builder = LLVMCreateBuilder();
    if (!builder) {
        fprintf(stderr, "Failed to create LLVM builder.\n");
        LLVMDisposeModule(module);
        LLVMContextDispose(context);
        return 1;
    }

    // 6. –ü–æ–ª—É—á–µ–Ω–∏–µ –±–ª–æ–∫–∞ entry –∏ –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ builder
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlock(fib_func, "entry");
    LLVMPositionBuilderAtEnd(builder, entry_block);

    // 7. –ü–æ–ª—É—á–µ–Ω–∏–µ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞ —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMValueRef n = LLVMGetParam(fib_func, 0);

    // 8. –ü—Ä–æ–≤–µ—Ä–∫–∞ —É—Å–ª–æ–≤–∏—è n <= 1
    LLVMValueRef cond = LLVMBuildICmp(builder, LLVMICMP_SLE, n, LLVMConstInt(i32_type, 1, 0), "cond");

    // 9. –°–æ–∑–¥–∞–Ω–∏–µ –±–ª–æ–∫–æ–≤ –¥–ª—è –≤–µ—Ç–≤–ª–µ–Ω–∏—è
    LLVMBasicBlockRef true_block = LLVMAppendBasicBlock(fib_func, "true");
    LLVMBasicBlockRef false_block = LLVMAppendBasicBlock(fib_func, "false");

    // 10. –£—Å–ª–æ–≤–Ω—ã–π –ø–µ—Ä–µ—Ö–æ–¥
    LLVMBuildCondBr(builder, cond, true_block, false_block);

    // 11. –û–±—Ä–∞–±–æ—Ç–∫–∞ –±–ª–æ–∫–∞ true (n <= 1)
    LLVMPositionBuilderAtEnd(builder, true_block);
    LLVMValueRef ret_true = LLVMBuildReturn(builder, n, "ret_true");

    // 12. –û–±—Ä–∞–±–æ—Ç–∫–∞ –±–ª–æ–∫–∞ false (—Ä–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π –≤—ã–∑–æ–≤)
