import numpy as np
from sklearn.linear_model import LogisticRegression
import random

class QNetwork:
    def __init__(self, state_size, action_size):
        self.model = LogisticRegression(solver='liblinear', max_iter=1000)
        self.state_size = state_size
        self.action_size = action_size

    def fit(self, states, actions, targets):
        self.model.fit(states, targets)

    def predict(self, states):
        return self.model.predict(states)

class ExperienceReplay:
    def __init__(self, capacity):
        self.capacity = capacity
        self.memory = []

    def add(self, state, action, reward, next_state, done):
        self.memory.append((state, action, reward, next_state, done))
        if len(self.memory) > self.capacity:
            self.memory.pop(0)

    def sample(self, batch_size):
        samples = random.sample(self.memory, batch_size)
        states, actions, rewards, next_states, dones = zip(*samples)
        return np.array(states), np.array(actions), np.array(rewards), np.array(next_states), np.array(dones)

def choose_action(model, state, epsilon):
    if random.random() < epsilon:
        return random.randint(0, model.action_size - 1)
    else:
        return model.predict(state.reshape(1, -1))[0]

def train_dqn(env, model, target_model, replay_buffer, batch_size, gamma, epsilon, episodes):
    for episode in range(episodes):
        state = env.reset()
        done = False
        while not done:
            action = choose_action(model, state, epsilon)
            next_state, reward, done, _ = env.step(action)
            replay_buffer.add(state, action, reward, next_state, done)
            if len(replay_buffer.memory) >= batch_size:
                states, actions, rewards, next_states, dones = replay_buffer.sample(batch_size)
                targets = rewards + (1 - dones) * gamma * target_model.predict(next_states)
                model.fit(states, targets)
            state = next_state
