        let mut string = shared_string_clone.lock().unwrap();
        *string = "Modified by thread".to_string();
        println!("Thread 1: {}", string);
    });

    // –ó–∞–ø—É—Å–∫–∞–µ–º –≤—Ç–æ—Ä–æ–π –ø–æ—Ç–æ–∫
    thread::spawn(move || {
        // –ó–∞—Ö–≤–∞—Ç—ã–≤–∞–µ–º –º—å—é—Ç–µ–∫—Å –¥–ª—è –∏–∑–º–µ–Ω–µ–Ω–∏—è —Å—Ç—Ä–æ–∫–∏
        let mut string = shared_string.lock().unwrap();
        *string = "Modified by thread 2".to_string();
        println!("Thread 2: {}", string);
    });

    // –û–∂–∏–¥–∞–µ–º –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –ø–æ—Ç–æ–∫–æ–≤
